import { useEffect, useState } from 'react'
import { CyanPhoneLayout } from './CyanPhoneLayout'
import { PurplePhoneLayout } from './PurplePhoneLayout'

// Layout theme options
// eslint-disable-next-line react-refresh/only-export-components
export const LAYOUT_THEMES = {
  CYAN: 'cyan',
  PURPLE: 'purple',
}

export function PhoneLayout() {
  const [layoutTheme, setLayoutTheme] = useState(LAYOUT_THEMES.CYAN)

  // Load layout preference from localStorage
  useEffect(() => {
    try {
      const saved = localStorage.getItem('app-settings')
      if (saved) {
        const settings = JSON.parse(saved)
        const savedLayout = settings.appearance?.layoutTheme
        if (savedLayout && Object.values(LAYOUT_THEMES).includes(savedLayout)) {
          setLayoutTheme(savedLayout)
        }
      }
    } catch (e) {
      console.error('Failed to load layout theme setting', e)
    }
  }, [])

  // Listen for layout theme changes
  useEffect(() => {
    const handleStorageChange = () => {
      try {
        const saved = localStorage.getItem('app-settings')
        if (saved) {
          const settings = JSON.parse(saved)
          const savedLayout = settings.appearance?.layoutTheme
          if (savedLayout && Object.values(LAYOUT_THEMES).includes(savedLayout)) {
            setLayoutTheme(savedLayout)
          }
        }
      } catch (e) {
        console.error('Failed to load layout theme setting', e)
      }
    }

    // Listen for custom event dispatched when settings change
    window.addEventListener('layout-theme-changed', handleStorageChange)
    // Also listen for storage changes from other tabs
    window.addEventListener('storage', handleStorageChange)

    return () => {
      window.removeEventListener('layout-theme-changed', handleStorageChange)
      window.removeEventListener('storage', handleStorageChange)
    }
  }, [])

  // Render the appropriate layout based on theme
  switch (layoutTheme) {
    case LAYOUT_THEMES.PURPLE:
      return <PurplePhoneLayout />
    case LAYOUT_THEMES.CYAN:
    default:
      return <CyanPhoneLayout />
  }
}

import { createContext, useContext, useState, useEffect, useCallback } from 'react'
import { supabase, isSupabaseConfigured } from '@/lib/supabase'
import { STORAGE_KEYS } from '@/lib/constants'

const AuthContext = createContext(null)

// Demo mode user for development without Supabase
const DEMO_USER = {
  id: 'demo-user-id',
  email: 'demo@messagehub.app',
  name: 'Demo User',
  avatar: null,
  created_at: new Date().toISOString(),
}

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null)
  const [isLoading, setIsLoading] = useState(true)
  const [isAuthenticated, setIsAuthenticated] = useState(false)

  // Transform Supabase user to app user format
  const transformUser = useCallback((supabaseUser) => {
    if (!supabaseUser) return null
    return {
      id: supabaseUser.id,
      email: supabaseUser.email,
      name: supabaseUser.user_metadata?.name || supabaseUser.email?.split('@')[0],
      avatar: supabaseUser.user_metadata?.avatar_url || null,
      created_at: supabaseUser.created_at,
    }
  }, [])

  // Initialize auth state
  useEffect(() => {
    let subscription = null

    const initAuth = async () => {
      if (isSupabaseConfigured && supabase) {
        // Get existing session from Supabase
        const {
          data: { session },
        } = await supabase.auth.getSession()

        if (session?.user) {
          const appUser = transformUser(session.user)
          setUser(appUser)
          setIsAuthenticated(true)
        }

        // Listen for auth state changes
        const {
          data: { subscription: authSubscription },
        } = supabase.auth.onAuthStateChange(async (_event, session) => {
          if (session?.user) {
            const appUser = transformUser(session.user)
            setUser(appUser)
            setIsAuthenticated(true)
          } else {
            setUser(null)
            setIsAuthenticated(false)
          }
        })

        subscription = authSubscription
      } else {
        // Demo mode: check localStorage
        const storedUser = localStorage.getItem(STORAGE_KEYS.USER)
        if (storedUser) {
          try {
            const parsedUser = JSON.parse(storedUser)
            setUser(parsedUser)
            setIsAuthenticated(true)
          } catch {
            localStorage.removeItem(STORAGE_KEYS.USER)
          }
        }
      }

      setIsLoading(false)
    }

    initAuth()

    // Cleanup subscription on unmount
    return () => {
      subscription?.unsubscribe()
    }
  }, [transformUser])

  // Login function
  const login = async (credentials) => {
    if (isSupabaseConfigured && supabase) {
      try {
        const { data, error } = await supabase.auth.signInWithPassword({
          email: credentials.email,
          password: credentials.password,
        })

        if (error) {
          return { success: false, error: error.message }
        }

        const appUser = transformUser(data.user)
        setUser(appUser)
        setIsAuthenticated(true)
        return { success: true }
      } catch {
        return { success: false, error: 'Login failed' }
      }
    } else {
      // Demo mode: accept any credentials
      const demoUser = {
        ...DEMO_USER,
        email: credentials.email,
        name: credentials.email.split('@')[0],
      }
      setUser(demoUser)
      setIsAuthenticated(true)
      localStorage.setItem(STORAGE_KEYS.USER, JSON.stringify(demoUser))
      return { success: true }
    }
  }

  // Register function
  const register = async (userData) => {
    if (isSupabaseConfigured && supabase) {
      try {
        const { data, error } = await supabase.auth.signUp({
          email: userData.email,
          password: userData.password,
          options: {
            data: {
              name: userData.name,
            },
          },
        })

        if (error) {
          return { success: false, error: error.message }
        }

        // Check if email confirmation is required
        if (data.user && !data.session) {
          return {
            success: true,
            message: 'Please check your email to confirm your account',
          }
        }

        const appUser = transformUser(data.user)
        setUser(appUser)
        setIsAuthenticated(true)
        return { success: true }
      } catch {
        return { success: false, error: 'Registration failed' }
      }
    } else {
      // Demo mode: simulate registration
      const demoUser = {
        ...DEMO_USER,
        name: userData.name,
        email: userData.email,
      }
      setUser(demoUser)
      setIsAuthenticated(true)
      localStorage.setItem(STORAGE_KEYS.USER, JSON.stringify(demoUser))
      return { success: true }
    }
  }

  // Logout function
  const logout = async () => {
    if (isSupabaseConfigured && supabase) {
      await supabase.auth.signOut()
    }

    // Clear local state
    setUser(null)
    setIsAuthenticated(false)
    localStorage.removeItem(STORAGE_KEYS.USER)
    localStorage.removeItem(STORAGE_KEYS.AUTH_TOKEN)
  }

  // Update profile function
  const updateProfile = async (profileData) => {
    if (isSupabaseConfigured && supabase) {
      try {
        const { data, error } = await supabase.auth.updateUser({
          data: profileData,
        })

        if (error) {
          return { success: false, error: error.message }
        }

        const appUser = transformUser(data.user)
        setUser(appUser)
        return { success: true }
      } catch {
        return { success: false, error: 'Profile update failed' }
      }
    } else {
      // Demo mode: update local storage
      const updatedUser = { ...user, ...profileData }
      setUser(updatedUser)
      localStorage.setItem(STORAGE_KEYS.USER, JSON.stringify(updatedUser))
      return { success: true }
    }
  }

  const value = {
    user,
    isLoading,
    isAuthenticated,
    login,
    logout,
    register,
    updateProfile,
    isSupabaseConfigured, // Expose for UI to show demo mode indicator
  }

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>
}

// eslint-disable-next-line react-refresh/only-export-components
export function useAuth() {
  const context = useContext(AuthContext)
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider')
  }
  return context
}

import { createContext, useContext, useState, useCallback } from 'react'
import { CallModal } from '@/components/common/CallModal'

const CallContext = createContext(null)

export function CallProvider({ children }) {
  const [call, setCall] = useState({
    isActive: false,
    status: 'idle', // idle, ringing, connected
    contact: null,
  })

  const startCall = useCallback((contact) => {
    setCall({
      isActive: true,
      status: 'ringing',
      contact,
    })

    // Simulate connection after 2 seconds
    setTimeout(() => {
      setCall((prev) => {
        if (prev.isActive) {
          return { ...prev, status: 'connected' }
        }
        return prev
      })
    }, 2000)
  }, [])

  const endCall = useCallback(() => {
    setCall({
      isActive: false,
      status: 'idle',
      contact: null,
    })
  }, [])

  return (
    <CallContext.Provider value={{ call, startCall, endCall }}>
      {children}
      <CallModal call={call} onEndCall={endCall} />
    </CallContext.Provider>
  )
}

// eslint-disable-next-line react-refresh/only-export-components
export function useCall() {
  const context = useContext(CallContext)
  if (!context) {
    throw new Error('useCall must be used within a CallProvider')
  }
  return context
}

import { cva } from 'class-variance-authority'

import { cn } from '@/lib/utils'

const badgeVariants = cva(
  'inline-flex items-center rounded-md border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2',
  {
    variants: {
      variant: {
        default: 'border-transparent bg-primary text-primary-foreground shadow hover:bg-primary/80',
        secondary:
          'border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80',
        destructive:
          'border-transparent bg-destructive text-destructive-foreground shadow hover:bg-destructive/80',
        outline: 'text-foreground',
      },
    },
    defaultVariants: {
      variant: 'default',
    },
  }
)

function Badge({ className, variant, ...props }) {
  return <div className={cn(badgeVariants({ variant }), className)} {...props} />
}

// eslint-disable-next-line react-refresh/only-export-components
export { Badge, badgeVariants }

import * as React from 'react'
import { Slot } from '@radix-ui/react-slot'
import { cva } from 'class-variance-authority'

import { cn } from '@/lib/utils'

const buttonVariants = cva(
  'inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:shrink-0 touch-manipulation',
  {
    variants: {
      variant: {
        default: 'bg-primary text-primary-foreground shadow hover:bg-primary/90',
        destructive: 'bg-destructive text-destructive-foreground shadow-sm hover:bg-destructive/90',
        outline:
          'border border-input bg-background shadow-sm hover:bg-accent hover:text-accent-foreground',
        secondary: 'bg-secondary text-secondary-foreground shadow-sm hover:bg-secondary/80',
        ghost: 'hover:bg-accent hover:text-accent-foreground',
        link: 'text-primary underline-offset-4 hover:underline',
      },
      size: {
        default: 'h-11 px-4 py-2 [&_svg]:size-5',
        sm: 'h-9 rounded-md px-3 text-xs [&_svg]:size-4',
        lg: 'h-12 rounded-md px-8 [&_svg]:size-5',
        icon: 'h-11 w-11 [&_svg]:size-5',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
    },
  }
)

const Button = React.forwardRef(({ className, variant, size, asChild = false, ...props }, ref) => {
  const Comp = asChild ? Slot : 'button'
  return <Comp className={cn(buttonVariants({ variant, size, className }))} ref={ref} {...props} />
})
Button.displayName = 'Button'

// eslint-disable-next-line react-refresh/only-export-components
export { Button, buttonVariants }

import { lazy, Suspense } from 'react'
import { Routes, Route, Navigate } from 'react-router-dom'
import { ROUTES } from '@/lib/constants'

// Layouts
import RootLayout from '@/components/layout/RootLayout'

// Lazy Load Pages
const LoginPage = lazy(() => import('@/pages/LoginPage'))
const RegisterPage = lazy(() => import('@/pages/RegisterPage'))
const ProfilePage = lazy(() => import('@/pages/ProfilePage'))
const NotFoundPage = lazy(() => import('@/pages/NotFoundPage'))

// Phone Pages
const PhoneDashboardPage = lazy(() =>
  import('@/pages/phone').then((module) => ({ default: module.PhoneDashboardPage }))
)
const PhoneChatsPage = lazy(() =>
  import('@/pages/phone').then((module) => ({ default: module.PhoneChatsPage }))
)
const PhoneContactsPage = lazy(() =>
  import('@/pages/phone').then((module) => ({ default: module.PhoneContactsPage }))
)
const PhoneSettingsPage = lazy(() =>
  import('@/pages/phone').then((module) => ({ default: module.PhoneSettingsPage }))
)
const MediaLibraryPage = lazy(() =>
  import('@/pages/phone').then((module) => ({ default: module.MediaLibraryPage }))
)

// Auth guard component
import { AuthGuard } from '@/features/auth/components/AuthGuard'

// Loading Component
const PageLoader = () => (
  <div className="flex items-center justify-center min-h-screen bg-background text-foreground">
    <div className="animate-pulse space-y-4 text-center">
      <div className="h-12 w-12 rounded-full bg-primary/20 mx-auto mb-4 animate-spin border-t-2 border-primary"></div>
      <p className="text-muted-foreground">Loading...</p>
    </div>
  </div>
)

export function AppRoutes() {
  return (
    <Suspense fallback={<PageLoader />}>
      <Routes>
        {/* Public routes */}
        <Route path={ROUTES.LOGIN} element={<LoginPage />} />
        <Route path={ROUTES.REGISTER} element={<RegisterPage />} />

        {/* Protected routes - Phone pages have built-in frame */}
        <Route element={<AuthGuard />}>
          <Route path={ROUTES.HOME} element={<Navigate to={ROUTES.DASHBOARD} replace />} />
          <Route path={ROUTES.DASHBOARD} element={<PhoneDashboardPage />} />
          <Route path={ROUTES.CONVERSATIONS} element={<PhoneChatsPage />} />
          <Route path={ROUTES.CHAT} element={<PhoneChatsPage />} />
          <Route path={ROUTES.CONTACTS} element={<PhoneContactsPage />} />
          <Route path={ROUTES.SETTINGS} element={<PhoneSettingsPage />} />
          <Route path={ROUTES.MEDIA_LIBRARY} element={<MediaLibraryPage />} />
          <Route element={<RootLayout />}>
            <Route path={ROUTES.PROFILE} element={<ProfilePage />} />
          </Route>
        </Route>

        {/* 404 */}
        <Route path="*" element={<NotFoundPage />} />
      </Routes>
    </Suspense>
  )
}

import { createContext, useContext, useState, useEffect, useCallback, useMemo } from 'react'

/**
 * Phone UI Theme System
 *
 * 4 theme variants with CSS custom properties for the phone-style UI.
 * Separate from next-themes (dark/light mode) - this handles the accent color schemes.
 */

const themes = {
  cyanDark: {
    name: 'Cyan Glow',
    bg: 'linear-gradient(145deg, #0a1628 0%, #0d1f35 50%, #0a1628 100%)',
    cardBg: 'rgba(6, 182, 212, 0.08)',
    cardBorder: 'rgba(6, 182, 212, 0.2)',
    accent: '#06b6d4',
    accentGlow: 'rgba(6, 182, 212, 0.4)',
    gradientStart: '#06b6d4',
    gradientEnd: '#0891b2',
    text: '#e2e8f0',
    textMuted: '#94a3b8',
    searchBg: 'rgba(6, 182, 212, 0.1)',
    navBg: 'rgba(6, 182, 212, 0.05)',
    isDark: true,
  },
  purpleDark: {
    name: 'Purple Glow',
    bg: 'linear-gradient(145deg, #1a0a28 0%, #2d1045 50%, #1a0a28 100%)',
    cardBg: 'rgba(168, 85, 247, 0.08)',
    cardBorder: 'rgba(168, 85, 247, 0.2)',
    accent: '#a855f7',
    accentGlow: 'rgba(168, 85, 247, 0.4)',
    gradientStart: '#a855f7',
    gradientEnd: '#9333ea',
    text: '#f3e8ff',
    textMuted: '#c4b5fd',
    searchBg: 'rgba(168, 85, 247, 0.1)',
    navBg: 'rgba(168, 85, 247, 0.05)',
    isDark: true,
  },
  cyanLight: {
    name: 'Soft Cyan',
    bg: 'linear-gradient(145deg, #ffffff 0%, #f0fdfa 50%, #ffffff 100%)',
    cardBg: 'rgba(6, 182, 212, 0.06)',
    cardBorder: 'rgba(6, 182, 212, 0.15)',
    accent: '#0891b2',
    accentGlow: 'rgba(6, 182, 212, 0.25)',
    gradientStart: '#06b6d4',
    gradientEnd: '#0891b2',
    text: '#164e63',
    textMuted: '#64748b',
    searchBg: 'rgba(6, 182, 212, 0.08)',
    navBg: 'rgba(255, 255, 255, 0.9)',
    isDark: false,
  },
  purpleLight: {
    name: 'Soft Purple',
    bg: 'linear-gradient(145deg, #ffffff 0%, #faf5ff 50%, #ffffff 100%)',
    cardBg: 'rgba(168, 85, 247, 0.06)',
    cardBorder: 'rgba(168, 85, 247, 0.15)',
    accent: '#9333ea',
    accentGlow: 'rgba(168, 85, 247, 0.25)',
    gradientStart: '#a855f7',
    gradientEnd: '#9333ea',
    text: '#581c87',
    textMuted: '#64748b',
    searchBg: 'rgba(168, 85, 247, 0.08)',
    navBg: 'rgba(255, 255, 255, 0.9)',
    isDark: false,
  },
}

const THEME_STORAGE_KEY = 'app-settings'
const DEFAULT_THEME = 'cyanDark'

const PhoneThemeContext = createContext(null)

/**
 * Inject CSS custom properties for the current theme
 */
function injectThemeCSS(themeKey) {
  const theme = themes[themeKey] || themes[DEFAULT_THEME]
  const root = document.documentElement

  root.style.setProperty('--phone-bg', theme.bg)
  root.style.setProperty('--phone-card-bg', theme.cardBg)
  root.style.setProperty('--phone-card-border', theme.cardBorder)
  root.style.setProperty('--phone-accent', theme.accent)
  root.style.setProperty('--phone-accent-glow', theme.accentGlow)
  root.style.setProperty('--phone-gradient-start', theme.gradientStart)
  root.style.setProperty('--phone-gradient-end', theme.gradientEnd)
  root.style.setProperty('--phone-text', theme.text)
  root.style.setProperty('--phone-text-muted', theme.textMuted)
  root.style.setProperty('--phone-search-bg', theme.searchBg)
  root.style.setProperty('--phone-nav-bg', theme.navBg)
  root.style.setProperty('--phone-is-dark', theme.isDark ? '1' : '0')
}

/**
 * Read the current theme from localStorage
 */
function getStoredTheme() {
  try {
    const saved = localStorage.getItem(THEME_STORAGE_KEY)
    if (saved) {
      const settings = JSON.parse(saved)
      const themeKey = settings.appearance?.colorTheme
      if (themeKey && themes[themeKey]) {
        return themeKey
      }
    }
  } catch (e) {
    console.error('Failed to read phone theme from storage:', e)
  }
  return DEFAULT_THEME
}

/**
 * Save the theme to localStorage
 */
function saveThemeToStorage(themeKey) {
  try {
    const saved = localStorage.getItem(THEME_STORAGE_KEY)
    const settings = saved ? JSON.parse(saved) : {}
    settings.appearance = settings.appearance || {}
    settings.appearance.colorTheme = themeKey
    localStorage.setItem(THEME_STORAGE_KEY, JSON.stringify(settings))
  } catch (e) {
    console.error('Failed to save phone theme to storage:', e)
  }
}

export function PhoneThemeProvider({ children }) {
  const [themeKey, setThemeKey] = useState(() => getStoredTheme())

  // Apply CSS variables on mount and theme change
  useEffect(() => {
    injectThemeCSS(themeKey)
  }, [themeKey])

  // Listen for cross-tab theme changes
  useEffect(() => {
    const handleStorage = (e) => {
      if (e.key === THEME_STORAGE_KEY) {
        const newTheme = getStoredTheme()
        setThemeKey(newTheme)
      }
    }

    // Listen for custom event from other components that change theme
    const handleThemeChanged = (e) => {
      if (e.detail?.theme && themes[e.detail.theme]) {
        setThemeKey(e.detail.theme)
      }
    }

    window.addEventListener('storage', handleStorage)
    window.addEventListener('layout-theme-changed', handleThemeChanged)

    return () => {
      window.removeEventListener('storage', handleStorage)
      window.removeEventListener('layout-theme-changed', handleThemeChanged)
    }
  }, [])

  // Change theme and persist
  const setTheme = useCallback((newThemeKey) => {
    if (themes[newThemeKey]) {
      setThemeKey(newThemeKey)
      saveThemeToStorage(newThemeKey)
      // Dispatch event for other components
      window.dispatchEvent(new CustomEvent('layout-theme-changed', {
        detail: { theme: newThemeKey }
      }))
    }
  }, [])

  // Get current theme object
  const theme = useMemo(() => themes[themeKey] || themes[DEFAULT_THEME], [themeKey])

  // Context value
  const value = useMemo(() => ({
    themeKey,
    theme,
    setTheme,
    themes,
    isDark: theme.isDark,
  }), [themeKey, theme, setTheme])

  return (
    <PhoneThemeContext.Provider value={value}>
      {children}
    </PhoneThemeContext.Provider>
  )
}

/**
 * Hook to access phone theme
 *
 * @returns {{
 *   themeKey: string,
 *   theme: object,
 *   setTheme: (key: string) => void,
 *   themes: object,
 *   isDark: boolean
 * }}
 */
export function usePhoneTheme() {
  const context = useContext(PhoneThemeContext)
  if (!context) {
    throw new Error('usePhoneTheme must be used within a PhoneThemeProvider')
  }
  return context
}

// Export themes for direct access if needed
export { themes as phoneThemes }

import { useRef, useCallback, useEffect } from 'react'
import { usePhoneTheme } from '@/context/PhoneThemeContext'
import { useStudioState } from './hooks/useStudioState'
import { StudioHeader } from './components/StudioHeader'
import { StudioCanvas } from './components/StudioCanvas'
import { StudioSidebar } from './components/StudioSidebar'
import { StudioLayersPanel } from './components/StudioLayersPanel'
import { StudioMobileControls } from './components/StudioMobileControls'
import { ExportOptionsModal, TemplateBrowserModal, PlatformPickerModal, VideoExportModal } from './components/modals'

/**
 * AIStudio - Main orchestrator component for the AI-powered design studio
 *
 * This is the entry point for the modular AI Studio feature.
 * It coordinates all sub-components and manages shared state.
 */
export function AIStudio({ onClose, onExport, onSendAsCampaign, isMobile = false }) {
  const { theme } = usePhoneTheme()
  const canvasRef = useRef(null)

  // Centralized state management
  const state = useStudioState({ defaultPlatform: 'instagram-post' })

  // Export handler
  const handleExport = useCallback(async () => {
    if (!canvasRef.current) return

    try {
      const dataUrl = await canvasRef.current.exportAsDataUrl('image/png', 1.0)
      if (dataUrl) {
        // Open export options modal
        state.openModal('export')
      }
    } catch (error) {
      console.error('Export failed:', error)
    }
  }, [state])

  // Download handler
  const handleDownload = useCallback(async () => {
    if (!canvasRef.current) return

    try {
      const dataUrl = await canvasRef.current.exportAsDataUrl('image/png', 1.0)
      if (dataUrl) {
        const link = document.createElement('a')
        link.download = `design-${Date.now()}.png`
        link.href = dataUrl
        link.click()
        state.closeModal('export')
        onExport?.(dataUrl)
      }
    } catch (error) {
      console.error('Download failed:', error)
    }
  }, [state, onExport])

  // Send as campaign handler
  const handleSendAsCampaign = useCallback(async () => {
    if (!canvasRef.current) return

    try {
      const dataUrl = await canvasRef.current.exportAsDataUrl('image/png', 1.0)
      if (dataUrl) {
        state.closeModal('export')
        onSendAsCampaign?.(dataUrl)
      }
    } catch (error) {
      console.error('Send as campaign failed:', error)
    }
  }, [state, onSendAsCampaign])

  // Primary text drag handlers
  const handlePrimaryTextDrag = useCallback(
    (position) => {
      state.setTextOverlay({ x: position.x, y: position.y })
    },
    [state]
  )

  // Layer drag handler
  const handleLayerDrag = useCallback(
    (layerId, position) => {
      state.updateLayer(layerId, { data: { x: position.x, y: position.y } }, true) // Skip history during drag
    },
    [state]
  )

  const handleLayerDragEnd = useCallback(
    (layerId) => {
      // Push to history after drag ends
      const layer = state.layers.find((l) => l.id === layerId)
      if (layer) {
        state.updateLayer(layerId, { data: layer.data })
      }
    },
    [state]
  )

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Undo: Ctrl+Z / Cmd+Z
      if ((e.ctrlKey || e.metaKey) && e.key === 'z' && !e.shiftKey) {
        e.preventDefault()
        state.undo()
      }
      // Redo: Ctrl+Shift+Z / Cmd+Shift+Z or Ctrl+Y
      if ((e.ctrlKey || e.metaKey) && (e.key === 'y' || (e.key === 'z' && e.shiftKey))) {
        e.preventDefault()
        state.redo()
      }
      // Escape: Close modals or studio
      if (e.key === 'Escape') {
        if (state.modals.export || state.modals.templates || state.modals.platforms || state.modals.videoExport) {
          state.closeAllModals()
        } else {
          onClose?.()
        }
      }
    }

    window.addEventListener('keydown', handleKeyDown)
    return () => window.removeEventListener('keydown', handleKeyDown)
  }, [state, onClose])

  return (
    <div
      style={{
        position: 'fixed',
        inset: 0,
        zIndex: 50,
        background: theme.bg,
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      {/* Header */}
      <StudioHeader
        currentPreset={state.currentPreset}
        exportWidth={state.exportWidth}
        exportHeight={state.exportHeight}
        onPlatformClick={() => state.openModal('platforms')}
        onUndo={state.undo}
        onRedo={state.redo}
        canUndo={state.canUndo}
        canRedo={state.canRedo}
        onExport={handleExport}
        onClose={onClose}
      />

      {/* Main content area */}
      <div style={{ flex: 1, display: 'flex', overflow: 'hidden' }}>
        {/* Desktop: Left Sidebar */}
        {!isMobile && (
          <StudioSidebar
            // Upload
            imageFile={state.imageFile}
            onImageUpload={state.setImageFile}
            // AI - Copy generation
            prompt={state.prompt}
            onPromptChange={state.setPrompt}
            onGenerate={state.generate}
            isGenerating={state.isGenerating}
            onAnalyzeImage={state.analyzeImage}
            isAnalyzing={state.isAnalyzing}
            // AI - Background generation (Phase 2)
            backgroundPrompt={state.backgroundPrompt}
            onBackgroundPromptChange={state.setBackgroundPrompt}
            onGenerateBackground={state.generateBackground}
            isGeneratingBackground={state.isGeneratingBackground}
            generatedBackground={state.generatedBackground}
            // AI - Subject processing (Phase 2)
            onRemoveBackground={state.removeBackground}
            isRemovingBackground={state.isRemovingBackground}
            subjectImage={state.subjectImage}
            // AI - Typography (Phase 2)
            onSuggestTypography={state.suggestTypography}
            isSuggestingTypography={state.isSuggestingTypography}
            // AI - Auto-level (Phase 2)
            onAutoLevel={state.autoLevel}
            isAutoLeveling={state.isAutoLeveling}
            // AI - Video generation (Phase 3)
            videoModel={state.videoModel}
            onVideoModelChange={state.setVideoModel}
            videoPrompt={state.videoPrompt}
            onVideoPromptChange={state.setVideoPrompt}
            onGenerateVideo={state.generateVideo}
            isGeneratingVideo={state.isGeneratingVideo}
            videoGenerationProgress={state.videoGenerationProgress}
            generatedVideoUrl={state.generatedVideoUrl}
            videoError={state.videoError}
            // Video overlays (Phase 3)
            videoOverlays={state.videoOverlays}
            selectedOverlayId={state.selectedOverlayId}
            onSelectOverlay={state.selectOverlay}
            onAddVideoOverlay={state.addVideoOverlay}
            onUpdateVideoOverlay={state.updateVideoOverlay}
            onRemoveVideoOverlay={state.removeVideoOverlay}
            videoDuration={state.videoDuration}
            // Video rendering (Phase 3)
            onRenderFinalVideo={state.renderFinalVideo}
            isRenderingVideo={state.isRenderingVideo}
            onOpenVideoExport={() => state.openModal('videoExport')}
            // Templates
            templates={state.marketingTemplates}
            activeTemplateId={state.activeTemplateId}
            onTemplateSelect={state.selectTemplate}
            onOpenTemplateLibrary={() => state.openModal('templates')}
            // Background
            background={state.background}
            onBackgroundChange={state.setBackground}
            // Text
            textOverlay={state.textOverlay}
            onTextChange={state.setTextOverlay}
          />
        )}

        {/* Canvas Area */}
        <div
          style={{
            flex: 1,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            background: theme.bg,
            padding: '20px',
            overflow: 'hidden',
          }}
        >
          <StudioCanvas
            ref={canvasRef}
            width={state.canvasWidth}
            height={state.canvasHeight}
            exportWidth={state.exportWidth}
            exportHeight={state.exportHeight}
            background={state.background}
            imageFile={state.imageFile}
            layers={state.sortedLayers}
            primaryTextOverlay={state.textOverlay}
            activeTemplate={state.activeTemplate}
            selectedLayerId={state.selectedLayerId}
            onLayerSelect={state.selectLayer}
            onPrimaryTextDrag={handlePrimaryTextDrag}
            onLayerDrag={handleLayerDrag}
            onLayerDragEnd={handleLayerDragEnd}
            style={{
              borderRadius: '12px',
              boxShadow: '0 8px 32px rgba(0,0,0,0.3)',
            }}
          />
        </div>

        {/* Desktop: Right Sidebar (Layers Panel) */}
        {!isMobile && (
          <StudioLayersPanel
            primaryText={state.textOverlay?.text}
            layers={state.layers}
            selectedLayerId={state.selectedLayerId}
            onSelectLayer={state.selectLayer}
            onToggleVisibility={state.toggleLayerVisibility}
            onRemoveLayer={state.removeLayer}
            onAddTextLayer={() => state.addTextLayer()}
            hasImage={!!state.imageFile}
            onResizeCanvas={() => state.openModal('platforms')}
            onResetAll={state.resetAll}
          />
        )}
      </div>

      {/* Mobile: Bottom Controls */}
      {isMobile && (
        <StudioMobileControls
          // Upload
          imageFile={state.imageFile}
          onImageUpload={state.setImageFile}
          // AI - Copy generation
          prompt={state.prompt}
          onPromptChange={state.setPrompt}
          onGenerate={state.generate}
          isGenerating={state.isGenerating}
          onAnalyzeImage={state.analyzeImage}
          isAnalyzing={state.isAnalyzing}
          // AI - Background generation (Phase 2)
          backgroundPrompt={state.backgroundPrompt}
          onBackgroundPromptChange={state.setBackgroundPrompt}
          onGenerateBackground={state.generateBackground}
          isGeneratingBackground={state.isGeneratingBackground}
          generatedBackground={state.generatedBackground}
          // AI - Subject processing (Phase 2)
          onRemoveBackground={state.removeBackground}
          isRemovingBackground={state.isRemovingBackground}
          subjectImage={state.subjectImage}
          // AI - Auto-level (Phase 2)
          onAutoLevel={state.autoLevel}
          isAutoLeveling={state.isAutoLeveling}
          // AI - Typography (Phase 2)
          onSuggestTypography={state.suggestTypography}
          isSuggestingTypography={state.isSuggestingTypography}
          // AI - Video generation (Phase 3)
          videoModel={state.videoModel}
          onVideoModelChange={state.setVideoModel}
          videoPrompt={state.videoPrompt}
          onVideoPromptChange={state.setVideoPrompt}
          onGenerateVideo={state.generateVideo}
          isGeneratingVideo={state.isGeneratingVideo}
          videoGenerationProgress={state.videoGenerationProgress}
          generatedVideoUrl={state.generatedVideoUrl}
          videoError={state.videoError}
          // Video overlays (Phase 3)
          videoOverlays={state.videoOverlays}
          selectedOverlayId={state.selectedOverlayId}
          onSelectOverlay={state.selectOverlay}
          onAddVideoOverlay={state.addVideoOverlay}
          onUpdateVideoOverlay={state.updateVideoOverlay}
          onRemoveVideoOverlay={state.removeVideoOverlay}
          videoDuration={state.videoDuration}
          // Video rendering (Phase 3)
          onOpenVideoExport={() => state.openModal('videoExport')}
          isRenderingVideo={state.isRenderingVideo}
          // Text
          textOverlay={state.textOverlay}
          onTextChange={state.setTextOverlay}
          // Templates
          templates={state.marketingTemplates}
          templateCategories={state.templateCategories}
          activeTemplateId={state.activeTemplateId}
          onTemplateSelect={state.selectTemplate}
          onClearTemplate={state.clearTemplate}
          // Platform
          platformPresets={state.platformPresets}
          selectedPlatform={state.selectedPlatform}
          currentPreset={state.currentPreset}
          exportWidth={state.exportWidth}
          exportHeight={state.exportHeight}
          onPlatformSelect={(id) => state.setSelectedPlatform(id)}
        />
      )}

      {/* Modals */}
      <ExportOptionsModal
        isOpen={state.modals.export}
        onClose={() => state.closeModal('export')}
        onDownload={handleDownload}
        onSendAsCampaign={handleSendAsCampaign}
        onContinueEditing={() => state.closeModal('export')}
      />

      <TemplateBrowserModal
        isOpen={state.modals.templates}
        onClose={() => state.closeModal('templates')}
        templates={state.marketingTemplates}
        categories={state.templateCategories}
        onSelect={(template) => {
          state.selectTemplate(template)
          state.closeModal('templates')
        }}
      />

      <PlatformPickerModal
        isOpen={state.modals.platforms}
        onClose={() => state.closeModal('platforms')}
        presets={state.platformPresets}
        selectedPlatformId={state.selectedPlatform}
        onSelect={(id) => {
          state.setSelectedPlatform(id)
          state.closeModal('platforms')
        }}
      />

      <VideoExportModal
        isOpen={state.modals.videoExport}
        onClose={() => state.closeModal('videoExport')}
        isRendering={state.isRenderingVideo}
        renderProgress={state.renderProgress}
        finalVideoUrl={state.finalVideoUrl}
        generatedVideoUrl={state.generatedVideoUrl}
        onRenderWithOverlays={state.renderFinalVideo}
        onDownload={() => {
          state.closeModal('videoExport')
        }}
        onSendAsCampaign={(videoUrl) => {
          onSendAsCampaign?.(videoUrl)
        }}
        hasOverlays={state.videoOverlays?.length > 0}
      />
    </div>
  )
}

export default AIStudio

import { useRef, useEffect, useCallback, forwardRef, useImperativeHandle } from 'react'
import { usePhoneTheme } from '@/context/PhoneThemeContext'
import { LayerType } from '@/hooks/useLayerManager'
import {
  drawBackground,
  drawText,
  drawImage,
  drawTemplateElements,
  drawGrid,
  drawSelectionIndicator,
  getTextBounds,
  loadImageFromFile,
  createExportCanvas,
  canvasToDataUrl,
  canvasToBlob,
} from '@/lib/canvasUtils'
import { FALLBACK_COLORS } from '../utils/studioConstants'

/**
 * StudioCanvas - Pure canvas rendering component for AI Studio
 *
 * Handles all canvas drawing and interaction, but no UI controls.
 * Receives all state as props and calls back on interactions.
 */
const StudioCanvas = forwardRef(function StudioCanvas(
  {
    // Dimensions
    width,
    height,
    exportWidth,
    exportHeight,

    // Content
    background,
    imageFile,
    layers = [],
    primaryTextOverlay,
    activeTemplate,

    // Selection
    selectedLayerId,
    onLayerSelect,

    // Drag handling
    onPrimaryTextDragStart,
    onPrimaryTextDrag,
    onPrimaryTextDragEnd,
    onLayerDragStart,
    onLayerDrag,
    onLayerDragEnd,

    // Style
    style,
    className,
  },
  ref
) {
  const canvasRef = useRef(null)
  const imageRef = useRef(null)
  const isDraggingRef = useRef(false)
  const dragTargetRef = useRef(null) // 'primary' or layer id
  const dragOffsetRef = useRef({ x: 0, y: 0 })

  const { theme, isDark } = usePhoneTheme()
  const fallbackBg = isDark ? FALLBACK_COLORS.dark : FALLBACK_COLORS.light
  const gridColor = isDark ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.08)'

  // Load image when file changes
  useEffect(() => {
    if (!imageFile) {
      imageRef.current = null
      return
    }

    loadImageFromFile(imageFile)
      .then((img) => {
        imageRef.current = img
      })
      .catch((err) => {
        console.error('Failed to load image:', err)
        imageRef.current = null
      })
  }, [imageFile])

  // Draw canvas
  const render = useCallback(() => {
    const canvas = canvasRef.current
    if (!canvas) return

    const ctx = canvas.getContext('2d')
    const w = canvas.width
    const h = canvas.height

    // 1. Draw background
    drawBackground(ctx, background, w, h, fallbackBg)

    // 2. Draw template elements (if no image)
    if (activeTemplate && !imageRef.current) {
      drawTemplateElements(ctx, activeTemplate, w, h)
    }

    // 3. Draw uploaded image
    if (imageRef.current) {
      drawImage(ctx, imageRef.current, w, h, 'contain')
    }

    // 4. Draw grid if empty (no image and no template)
    if (!imageRef.current && !activeTemplate) {
      drawGrid(ctx, w, h, gridColor, 40)
    }

    // 5. Draw primary text overlay
    if (primaryTextOverlay?.text) {
      drawText(ctx, {
        text: primaryTextOverlay.text,
        x: primaryTextOverlay.x,
        y: primaryTextOverlay.y,
        color: primaryTextOverlay.color,
        fontSize: primaryTextOverlay.fontSize,
        fontWeight: 'bold',
        textAlign: 'center',
        shadow: {
          color: 'rgba(0,0,0,0.5)',
          blur: 8,
          offsetX: 2,
          offsetY: 2,
        },
      })
    }

    // 6. Draw additional layers
    layers.forEach((layer) => {
      if (layer.type !== LayerType.TEXT || !layer.visible || !layer.data?.text) return

      const x = (layer.data.x / 100) * w
      const y = (layer.data.y / 100) * h

      drawText(ctx, {
        text: layer.data.text,
        x,
        y,
        color: layer.data.color,
        fontSize: layer.data.fontSize,
        fontWeight: layer.data.fontWeight || 'bold',
        textAlign: 'center',
        shadow: {
          color: 'rgba(0,0,0,0.5)',
          blur: 8,
          offsetX: 2,
          offsetY: 2,
        },
      })

      // Draw selection indicator
      if (layer.id === selectedLayerId) {
        const bounds = getTextBounds(ctx, {
          text: layer.data.text,
          x,
          y,
          fontSize: layer.data.fontSize,
          fontWeight: layer.data.fontWeight || 'bold',
        })
        drawSelectionIndicator(ctx, bounds, theme.accent)
      }
    })
  }, [
    background,
    activeTemplate,
    primaryTextOverlay,
    layers,
    selectedLayerId,
    fallbackBg,
    gridColor,
    theme.accent,
  ])

  // Re-render when dependencies change
  useEffect(() => {
    render()
  }, [render, imageFile, width, height])

  // Re-render when image loads
  useEffect(() => {
    if (!imageFile) return

    const checkImage = () => {
      if (imageRef.current) {
        render()
      }
    }

    // Poll for image load (since it's async)
    const interval = setInterval(checkImage, 100)
    const timeout = setTimeout(() => clearInterval(interval), 3000)

    return () => {
      clearInterval(interval)
      clearTimeout(timeout)
    }
  }, [imageFile, render])

  // Get canvas coordinates from event
  const getCanvasCoords = useCallback((e) => {
    const canvas = canvasRef.current
    if (!canvas) return { x: 0, y: 0 }

    const rect = canvas.getBoundingClientRect()
    const scaleX = canvas.width / rect.width
    const scaleY = canvas.height / rect.height

    const clientX = e.touches ? e.touches[0].clientX : e.clientX
    const clientY = e.touches ? e.touches[0].clientY : e.clientY

    return {
      x: (clientX - rect.left) * scaleX,
      y: (clientY - rect.top) * scaleY,
    }
  }, [])

  // Check if point is near primary text overlay
  const isNearPrimaryText = useCallback(
    (x, y) => {
      if (!primaryTextOverlay?.text) return false
      const distance = Math.sqrt(
        Math.pow(x - primaryTextOverlay.x, 2) + Math.pow(y - primaryTextOverlay.y, 2)
      )
      return distance < 150 // Hit radius
    },
    [primaryTextOverlay]
  )

  // Check if point is on a layer
  const getLayerAtPoint = useCallback(
    (x, y) => {
      const canvas = canvasRef.current
      if (!canvas) return null

      const ctx = canvas.getContext('2d')

      // Check layers in reverse order (top first)
      for (let i = layers.length - 1; i >= 0; i--) {
        const layer = layers[i]
        if (layer.type !== LayerType.TEXT || !layer.visible || !layer.data?.text) continue

        const layerX = (layer.data.x / 100) * canvas.width
        const layerY = (layer.data.y / 100) * canvas.height

        const bounds = getTextBounds(ctx, {
          text: layer.data.text,
          x: layerX,
          y: layerY,
          fontSize: layer.data.fontSize,
          fontWeight: layer.data.fontWeight || 'bold',
        })

        const padding = 20
        if (
          x >= bounds.x - padding &&
          x <= bounds.x + bounds.width + padding &&
          y >= bounds.y - padding &&
          y <= bounds.y + bounds.height + padding
        ) {
          return layer
        }
      }

      return null
    },
    [layers]
  )

  // Mouse/touch handlers
  const handlePointerDown = useCallback(
    (e) => {
      const coords = getCanvasCoords(e)

      // Check primary text first
      if (isNearPrimaryText(coords.x, coords.y)) {
        isDraggingRef.current = true
        dragTargetRef.current = 'primary'
        dragOffsetRef.current = {
          x: coords.x - primaryTextOverlay.x,
          y: coords.y - primaryTextOverlay.y,
        }
        onPrimaryTextDragStart?.()
        return
      }

      // Check layers
      const layer = getLayerAtPoint(coords.x, coords.y)
      if (layer) {
        isDraggingRef.current = true
        dragTargetRef.current = layer.id
        const canvas = canvasRef.current
        const layerX = (layer.data.x / 100) * canvas.width
        const layerY = (layer.data.y / 100) * canvas.height
        dragOffsetRef.current = {
          x: coords.x - layerX,
          y: coords.y - layerY,
        }
        onLayerSelect?.(layer.id)
        onLayerDragStart?.(layer.id)
        return
      }

      // Click on empty space - clear selection
      onLayerSelect?.(null)
    },
    [
      getCanvasCoords,
      isNearPrimaryText,
      getLayerAtPoint,
      primaryTextOverlay,
      onPrimaryTextDragStart,
      onLayerSelect,
      onLayerDragStart,
    ]
  )

  const handlePointerMove = useCallback(
    (e) => {
      if (!isDraggingRef.current) return

      const coords = getCanvasCoords(e)
      const canvas = canvasRef.current
      if (!canvas) return

      if (dragTargetRef.current === 'primary') {
        onPrimaryTextDrag?.({
          x: coords.x - dragOffsetRef.current.x,
          y: coords.y - dragOffsetRef.current.y,
        })
      } else if (dragTargetRef.current) {
        // Layer drag - convert to percentage
        onLayerDrag?.(dragTargetRef.current, {
          x: ((coords.x - dragOffsetRef.current.x) / canvas.width) * 100,
          y: ((coords.y - dragOffsetRef.current.y) / canvas.height) * 100,
        })
      }
    },
    [getCanvasCoords, onPrimaryTextDrag, onLayerDrag]
  )

  const handlePointerUp = useCallback(() => {
    if (isDraggingRef.current) {
      if (dragTargetRef.current === 'primary') {
        onPrimaryTextDragEnd?.()
      } else if (dragTargetRef.current) {
        onLayerDragEnd?.(dragTargetRef.current)
      }
    }

    isDraggingRef.current = false
    dragTargetRef.current = null
    dragOffsetRef.current = { x: 0, y: 0 }
  }, [onPrimaryTextDragEnd, onLayerDragEnd])

  // Expose methods via ref
  useImperativeHandle(
    ref,
    () => ({
      // Get the canvas element
      getCanvas: () => canvasRef.current,

      // Force re-render
      render,

      // Export at full resolution
      async exportAsDataUrl(format = 'image/png', quality = 1.0) {
        const canvas = canvasRef.current
        if (!canvas) return null

        const exportCanvas = createExportCanvas(
          canvas,
          exportWidth,
          exportHeight,
          (ctx, scaleX, scaleY, w, h) => {
            // Draw background
            drawBackground(ctx, background, w, h, fallbackBg)

            // Draw template
            if (activeTemplate && !imageRef.current) {
              drawTemplateElements(ctx, activeTemplate, w, h)
            }

            // Draw image
            if (imageRef.current) {
              drawImage(ctx, imageRef.current, w, h, 'contain')
            }

            // Draw primary text (scaled)
            if (primaryTextOverlay?.text) {
              drawText(ctx, {
                text: primaryTextOverlay.text,
                x: primaryTextOverlay.x * scaleX,
                y: primaryTextOverlay.y * scaleY,
                color: primaryTextOverlay.color,
                fontSize: primaryTextOverlay.fontSize * scaleX,
                fontWeight: 'bold',
                textAlign: 'center',
                shadow: {
                  color: 'rgba(0,0,0,0.5)',
                  blur: 8 * scaleX,
                  offsetX: 2 * scaleX,
                  offsetY: 2 * scaleY,
                },
              })
            }

            // Draw layers (scaled)
            layers.forEach((layer) => {
              if (layer.type !== LayerType.TEXT || !layer.visible || !layer.data?.text) return

              const x = (layer.data.x / 100) * w
              const y = (layer.data.y / 100) * h

              drawText(ctx, {
                text: layer.data.text,
                x,
                y,
                color: layer.data.color,
                fontSize: layer.data.fontSize * scaleX,
                fontWeight: layer.data.fontWeight || 'bold',
                textAlign: 'center',
                shadow: {
                  color: 'rgba(0,0,0,0.5)',
                  blur: 8 * scaleX,
                  offsetX: 2 * scaleX,
                  offsetY: 2 * scaleY,
                },
              })
            })
          }
        )

        return canvasToDataUrl(exportCanvas, format, quality)
      },

      // Export as blob
      async exportAsBlob(format = 'image/png', quality = 1.0) {
        const canvas = canvasRef.current
        if (!canvas) return null

        const exportCanvas = createExportCanvas(
          canvas,
          exportWidth,
          exportHeight,
          (ctx, scaleX, scaleY, w, h) => {
            // Same drawing logic as exportAsDataUrl
            drawBackground(ctx, background, w, h, fallbackBg)

            if (activeTemplate && !imageRef.current) {
              drawTemplateElements(ctx, activeTemplate, w, h)
            }

            if (imageRef.current) {
              drawImage(ctx, imageRef.current, w, h, 'contain')
            }

            if (primaryTextOverlay?.text) {
              drawText(ctx, {
                text: primaryTextOverlay.text,
                x: primaryTextOverlay.x * scaleX,
                y: primaryTextOverlay.y * scaleY,
                color: primaryTextOverlay.color,
                fontSize: primaryTextOverlay.fontSize * scaleX,
                fontWeight: 'bold',
                textAlign: 'center',
                shadow: {
                  color: 'rgba(0,0,0,0.5)',
                  blur: 8 * scaleX,
                  offsetX: 2 * scaleX,
                  offsetY: 2 * scaleY,
                },
              })
            }

            layers.forEach((layer) => {
              if (layer.type !== LayerType.TEXT || !layer.visible || !layer.data?.text) return

              const x = (layer.data.x / 100) * w
              const y = (layer.data.y / 100) * h

              drawText(ctx, {
                text: layer.data.text,
                x,
                y,
                color: layer.data.color,
                fontSize: layer.data.fontSize * scaleX,
                fontWeight: layer.data.fontWeight || 'bold',
                textAlign: 'center',
                shadow: {
                  color: 'rgba(0,0,0,0.5)',
                  blur: 8 * scaleX,
                  offsetX: 2 * scaleX,
                  offsetY: 2 * scaleY,
                },
              })
            })
          }
        )

        return canvasToBlob(exportCanvas, format, quality)
      },
    }),
    [
      render,
      exportWidth,
      exportHeight,
      background,
      fallbackBg,
      activeTemplate,
      primaryTextOverlay,
      layers,
    ]
  )

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={height}
      style={{
        cursor: isDraggingRef.current ? 'grabbing' : 'grab',
        touchAction: 'none',
        ...style,
      }}
      className={className}
      onMouseDown={handlePointerDown}
      onMouseMove={handlePointerMove}
      onMouseUp={handlePointerUp}
      onMouseLeave={handlePointerUp}
      onTouchStart={handlePointerDown}
      onTouchMove={handlePointerMove}
      onTouchEnd={handlePointerUp}
    />
  )
})

