import { usePhoneTheme } from '@/context/PhoneThemeContext'
import { StudioIcons } from '../utils/StudioIcons'

/**
 * StudioHeader - Top header bar for AI Studio
 *
 * Contains:
 * - Close button
 * - Platform selector dropdown
 * - Undo/Redo buttons
 * - Export button
 */
export function StudioHeader({
  currentPreset,
  exportWidth,
  exportHeight,
  onPlatformClick,
  onUndo,
  onRedo,
  canUndo,
  canRedo,
  onExport,
  onClose,
}) {
  const { theme } = usePhoneTheme()

  return (
    <div
      style={{
        height: '60px',
        borderBottom: `1px solid ${theme.cardBorder}`,
        background: theme.navBg,
        backdropFilter: 'blur(20px)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0 20px',
      }}
    >
      {/* Left: Close button */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
        <button
          onClick={onClose}
          style={{
            background: 'none',
            border: 'none',
            cursor: 'pointer',
            padding: '8px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          {StudioIcons.x(theme.textMuted, 24)}
        </button>
        <span
          style={{
            color: theme.text,
            fontSize: '16px',
            fontWeight: '600',
          }}
        >
          AI Studio
        </span>
      </div>

      {/* Center: Platform selector */}
      <button
        onClick={onPlatformClick}
        style={{
          display: 'flex',
          alignItems: 'center',
          gap: '8px',
          padding: '8px 16px',
          borderRadius: '8px',
          border: `1px solid ${theme.cardBorder}`,
          background: theme.cardBg,
          cursor: 'pointer',
        }}
      >
        <span style={{ color: theme.text, fontSize: '14px', fontWeight: '500' }}>
          {currentPreset?.label || 'Select Platform'}
        </span>
        <span style={{ color: theme.textMuted, fontSize: '12px' }}>
          {exportWidth} × {exportHeight}
        </span>
        {StudioIcons.chevronDown(theme.textMuted, 16)}
      </button>

      {/* Right: Actions */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
        {/* Undo */}
        <button
          onClick={onUndo}
          disabled={!canUndo}
          style={{
            background: 'none',
            border: 'none',
            cursor: canUndo ? 'pointer' : 'not-allowed',
            padding: '8px',
            opacity: canUndo ? 1 : 0.4,
          }}
          title="Undo"
        >
          {StudioIcons.undo(theme.textMuted, 20)}
        </button>

        {/* Redo */}
        <button
          onClick={onRedo}
          disabled={!canRedo}
          style={{
            background: 'none',
            border: 'none',
            cursor: canRedo ? 'pointer' : 'not-allowed',
            padding: '8px',
            opacity: canRedo ? 1 : 0.4,
          }}
          title="Redo"
        >
          {StudioIcons.redo(theme.textMuted, 20)}
        </button>

        {/* Export button */}
        <button
          onClick={onExport}
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            padding: '10px 20px',
            borderRadius: '10px',
            border: 'none',
            background: `linear-gradient(135deg, ${theme.gradientStart}, ${theme.gradientEnd})`,
            color: '#fff',
            fontSize: '14px',
            fontWeight: '600',
            cursor: 'pointer',
            boxShadow: `0 4px 12px ${theme.accentGlow}`,
          }}
        >
          {StudioIcons.download('#fff', 18)}
          Export
        </button>
      </div>
    </div>
  )
}

export default StudioHeader

import { usePhoneTheme } from '@/context/PhoneThemeContext'
import { LayerType } from '@/hooks/useLayerManager'
import { StudioIcons } from '../utils/StudioIcons'

/**
 * StudioLayersPanel - Right sidebar for layer management
 *
 * Shows:
 * - Primary text layer
 * - Additional text layers
 * - Image layer
 * - Background layer
 * - Add layer button
 * - Quick actions
 */
export function StudioLayersPanel({
  // Primary text (legacy)
  primaryText,

  // Additional layers
  layers = [],
  selectedLayerId,
  onSelectLayer,
  onToggleVisibility,
  onRemoveLayer,
  onAddTextLayer,

  // Image
  hasImage,

  // Quick actions
  onResizeCanvas,
  onResetAll,
}) {
  const { theme } = usePhoneTheme()

  const textLayers = layers.filter((l) => l.type === LayerType.TEXT)

  return (
    <div
      style={{
        width: '280px',
        borderLeft: `1px solid ${theme.cardBorder}`,
        background: theme.navBg,
        backdropFilter: 'blur(20px)',
        padding: '20px',
        display: 'flex',
        flexDirection: 'column',
        gap: '24px',
      }}
    >
      {/* Layers Section */}
      <div>
        <h3
          style={{
            color: theme.text,
            fontSize: '14px',
            fontWeight: '600',
            marginBottom: '12px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
          }}
        >
          {StudioIcons.layers(theme.accent, 18)} Layers
        </h3>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
          {/* Primary Text Layer (legacy) */}
          <div
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              padding: '12px',
              borderRadius: '12px',
              background: theme.cardBg,
              border: `1px solid ${theme.cardBorder}`,
              opacity: primaryText ? 1 : 0.5,
            }}
          >
            {StudioIcons.type(theme.textMuted, 18)}
            <span style={{ color: theme.text, fontSize: '13px', flex: 1 }}>
              {primaryText
                ? primaryText.substring(0, 20) + (primaryText.length > 20 ? '...' : '')
                : 'Text Layer'}
            </span>
            {primaryText
              ? StudioIcons.eye(theme.textMuted)
              : StudioIcons.eyeOff(theme.textMuted)}
          </div>

          {/* Additional Text Layers */}
          {textLayers.map((layer) => (
            <div
              key={layer.id}
              onClick={() => onSelectLayer(layer.id)}
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '12px',
                padding: '12px',
                borderRadius: '12px',
                background: selectedLayerId === layer.id ? `${theme.accent}20` : theme.cardBg,
                border: `1px solid ${selectedLayerId === layer.id ? theme.accent : theme.cardBorder}`,
                opacity: layer.visible ? 1 : 0.5,
                cursor: 'pointer',
                transition: 'all 0.2s ease',
              }}
            >
              {StudioIcons.type(theme.textMuted, 18)}
              <span style={{ color: theme.text, fontSize: '13px', flex: 1 }}>
                {layer.data?.text
                  ? layer.data.text.substring(0, 20) +
                    (layer.data.text.length > 20 ? '...' : '')
                  : layer.name}
              </span>
              <button
                onClick={(e) => {
                  e.stopPropagation()
                  onToggleVisibility(layer.id)
                }}
                style={{
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  padding: '4px',
                }}
              >
                {layer.visible
                  ? StudioIcons.eye(theme.textMuted)
                  : StudioIcons.eyeOff(theme.textMuted)}
              </button>
              <button
                onClick={(e) => {
                  e.stopPropagation()
                  onRemoveLayer(layer.id)
                }}
                style={{
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  padding: '4px',
                  color: '#ef4444',
                }}
              >
                {StudioIcons.x('#ef4444', 16)}
              </button>
            </div>
          ))}

          {/* Image Layer */}
          <div
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              padding: '12px',
              borderRadius: '12px',
              background: theme.cardBg,
              border: `1px solid ${theme.cardBorder}`,
              opacity: hasImage ? 1 : 0.5,
            }}
          >
            {StudioIcons.image(theme.textMuted, 18)}
            <span style={{ color: theme.text, fontSize: '13px', flex: 1 }}>Image Layer</span>
            {hasImage
              ? StudioIcons.eye(theme.textMuted)
              : StudioIcons.eyeOff(theme.textMuted)}
          </div>

          {/* Background Layer */}
          <div
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              padding: '12px',
              borderRadius: '12px',
              background: theme.cardBg,
              border: `1px solid ${theme.cardBorder}`,
            }}
          >
            {StudioIcons.grid(theme.textMuted, 18)}
            <span style={{ color: theme.text, fontSize: '13px', flex: 1 }}>Background</span>
            {StudioIcons.eye(theme.textMuted)}
          </div>

          {/* Add Text Layer Button */}
          <button
            onClick={onAddTextLayer}
            style={{
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px',
              padding: '12px',
              borderRadius: '12px',
              background: 'transparent',
              border: `1px dashed ${theme.cardBorder}`,
              color: theme.accent,
              fontSize: '13px',
              cursor: 'pointer',
              transition: 'all 0.2s ease',
            }}
          >
            {StudioIcons.plus(theme.accent, 16)} Add Text Layer
          </button>
        </div>
      </div>

      {/* Quick Actions Section */}
      <div>
        <h3
          style={{
            color: theme.text,
            fontSize: '14px',
            fontWeight: '600',
            marginBottom: '12px',
          }}
        >
          Quick Actions
        </h3>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
          <button
            onClick={onResizeCanvas}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              padding: '12px',
              borderRadius: '12px',
              background: 'transparent',
              border: `1px solid ${theme.cardBorder}`,
              color: theme.text,
              fontSize: '13px',
              cursor: 'pointer',
              textAlign: 'left',
            }}
          >
            {StudioIcons.resize(theme.textMuted, 18)} Resize Canvas
          </button>

          <button
            onClick={onResetAll}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              padding: '12px',
              borderRadius: '12px',
              background: 'transparent',
              border: `1px solid ${theme.cardBorder}`,
              color: theme.text,
              fontSize: '13px',
              cursor: 'pointer',
              textAlign: 'left',
            }}
          >
            {StudioIcons.x(theme.textMuted, 18)} Reset All
          </button>
        </div>
      </div>
    </div>
  )
}

export default StudioLayersPanel

