import { useState } from 'react'
import { usePhoneTheme } from '@/context/PhoneThemeContext'
import { StudioIcons } from '../utils/StudioIcons'
import { TEXT_COLORS } from '../utils/studioConstants'
import { VideoGenerationPanel } from './VideoGenerationPanel'
import { VideoOverlayEditor } from './VideoOverlayEditor'

/**
 * StudioMobileControls - Mobile tabbed control panel
 *
 * Bottom panel with tabs for:
 * - Upload: File input for images
 * - AI: Copy generation, background generation, subject processing
 * - Templates: Category filter and template grid
 * - Text: Input, color picker, font size slider, AI typography
 * - Size: Platform presets grid
 */
export function StudioMobileControls({
  // Upload
  imageFile,
  onImageUpload,

  // AI - Copy generation
  prompt,
  onPromptChange,
  onGenerate,
  isGenerating,
  onAnalyzeImage,
  isAnalyzing,

  // AI - Background generation (Phase 2)
  backgroundPrompt,
  onBackgroundPromptChange,
  onGenerateBackground,
  isGeneratingBackground,
  generatedBackground,

  // AI - Subject processing (Phase 2)
  onRemoveBackground,
  isRemovingBackground,
  subjectImage,

  // AI - Auto-level (Phase 2)
  onAutoLevel,
  isAutoLeveling,

  // AI - Typography (Phase 2)
  onSuggestTypography,
  isSuggestingTypography,

  // Text
  textOverlay,
  onTextChange,

  // Templates
  templates = [],
  templateCategories = [],
  activeTemplateId,
  onTemplateSelect,
  onClearTemplate,

  // Platform
  platformPresets = {},
  selectedPlatform,
  currentPreset,
  exportWidth,
  exportHeight,
  onPlatformSelect,

  // AI - Video generation (Phase 3)
  videoModel,
  onVideoModelChange,
  videoPrompt,
  onVideoPromptChange,
  onGenerateVideo,
  isGeneratingVideo,
  videoGenerationProgress,
  generatedVideoUrl,
  videoError,
  // Video overlays (Phase 3)
  videoOverlays,
  selectedOverlayId,
  onSelectOverlay,
  onAddVideoOverlay,
  onUpdateVideoOverlay,
  onRemoveVideoOverlay,
  videoDuration,
  // Video rendering (Phase 3)
  onOpenVideoExport,
  isRenderingVideo,
}) {
  const { theme } = usePhoneTheme()
  const [activeTab, setActiveTab] = useState('upload')
  const [templateCategory, setTemplateCategory] = useState('all')

  // Filter templates by category
  const filteredTemplates =
    templateCategory === 'all'
      ? templates
      : templates.filter((t) => t.category === templateCategory)

  const tabs = [
    { id: 'upload', icon: StudioIcons.upload, label: 'Upload' },
    { id: 'ai', icon: StudioIcons.sparkles, label: 'AI' },
    { id: 'video', icon: StudioIcons.video, label: 'Video' },
    { id: 'templates', icon: StudioIcons.layers, label: 'Templates' },
    { id: 'text', icon: StudioIcons.type, label: 'Text' },
    { id: 'size', icon: StudioIcons.grid, label: 'Size' },
  ]

  return (
    <div
      style={{
        background: theme.navBg,
        borderTop: `1px solid ${theme.cardBorder}`,
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      {/* Control Panel Content */}
      <div
        style={{
          height: '180px',
          overflowY: 'auto',
          padding: '16px',
          borderBottom: `1px solid ${theme.cardBorder}`,
        }}
      >
        {/* Upload Tab */}
        {activeTab === 'upload' && (
          <label
            style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: '12px',
              padding: '24px',
              borderRadius: '16px',
              border: `2px dashed ${theme.cardBorder}`,
              background: theme.cardBg,
              cursor: 'pointer',
            }}
          >
            <input
              type="file"
              accept="image/*,video/*"
              onChange={(e) => e.target.files?.[0] && onImageUpload(e.target.files[0])}
              style={{ display: 'none' }}
            />
            <div
              style={{
                width: '48px',
                height: '48px',
                borderRadius: '50%',
                background: `linear-gradient(135deg, ${theme.gradientStart}33, ${theme.gradientEnd}33)`,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
              }}
            >
              {StudioIcons.upload(theme.accent)}
            </div>
            <div style={{ textAlign: 'center' }}>
              <p style={{ color: theme.text, fontSize: '14px', fontWeight: '600', margin: 0 }}>
                {imageFile ? imageFile.name : 'Tap to upload image'}
              </p>
              <p style={{ color: theme.textMuted, fontSize: '12px', margin: '4px 0 0' }}>
                PNG, JPG up to 10MB
              </p>
            </div>
          </label>
        )}

        {/* AI Magic Tab */}
        {activeTab === 'ai' && (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
            {/* Copy Generation */}
            <input
              type="text"
              placeholder="Describe your marketing content..."
              value={prompt}
              onChange={(e) => onPromptChange(e.target.value)}
              style={{
                padding: '12px 14px',
                borderRadius: '12px',
                border: `1px solid ${theme.cardBorder}`,
                background: theme.searchBg,
                color: theme.text,
                fontSize: '14px',
                outline: 'none',
              }}
            />
            <button
              onClick={onGenerate}
              disabled={isGenerating || !prompt}
              style={{
                padding: '12px',
                borderRadius: '12px',
                border: 'none',
                background:
                  isGenerating || !prompt
                    ? theme.cardBg
                    : `linear-gradient(135deg, ${theme.gradientStart}, ${theme.gradientEnd})`,
                color: isGenerating || !prompt ? theme.textMuted : '#fff',
                fontSize: '13px',
                fontWeight: '600',
                cursor: isGenerating || !prompt ? 'not-allowed' : 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px',
              }}
            >
              {isGenerating ? 'Generating...' : <>{StudioIcons.sparkles('#fff', 16)} Generate Copy</>}
            </button>

            {/* Analyze Image Button */}
            {imageFile && (
              <button
                onClick={onAnalyzeImage}
                disabled={isAnalyzing}
                style={{
                  padding: '10px',
                  borderRadius: '10px',
                  border: `1px solid ${theme.cardBorder}`,
                  background: isAnalyzing ? theme.cardBg : `${theme.accent}15`,
                  color: isAnalyzing ? theme.textMuted : theme.accent,
                  fontSize: '12px',
                  fontWeight: '500',
                  cursor: isAnalyzing ? 'not-allowed' : 'pointer',
                }}
              >
                {isAnalyzing ? 'Analyzing...' : 'Analyze Image'}
              </button>
            )}

            {/* Background Generation (Phase 2) */}
            <div style={{ borderTop: `1px solid ${theme.cardBorder}`, paddingTop: '12px' }}>
              <span style={{ color: theme.textMuted, fontSize: '11px', marginBottom: '8px', display: 'block' }}>
                AI Background
              </span>
              <input
                type="text"
                placeholder="Describe background..."
                value={backgroundPrompt || ''}
                onChange={(e) => onBackgroundPromptChange?.(e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px 12px',
                  borderRadius: '10px',
                  border: `1px solid ${theme.cardBorder}`,
                  background: theme.searchBg,
                  color: theme.text,
                  fontSize: '13px',
                  outline: 'none',
                  marginBottom: '8px',
                  boxSizing: 'border-box',
                }}
              />
              <button
                onClick={onGenerateBackground}
                disabled={isGeneratingBackground || !backgroundPrompt}
                style={{
                  width: '100%',
                  padding: '10px',
                  borderRadius: '10px',
                  border: 'none',
                  background:
                    isGeneratingBackground || !backgroundPrompt
                      ? theme.cardBg
                      : `linear-gradient(135deg, ${theme.gradientStart}, ${theme.gradientEnd})`,
                  color: isGeneratingBackground || !backgroundPrompt ? theme.textMuted : '#fff',
                  fontSize: '12px',
                  fontWeight: '600',
                  cursor: isGeneratingBackground || !backgroundPrompt ? 'not-allowed' : 'pointer',
                }}
              >
                {isGeneratingBackground ? 'Generating...' : 'Generate Background'}
              </button>
              {generatedBackground && (
                <div style={{ padding: '6px', borderRadius: '6px', background: `${theme.accent}15`, color: theme.accent, fontSize: '11px', textAlign: 'center', marginTop: '6px' }}>
                  AI background applied
                </div>
              )}
            </div>

            {/* Subject Processing (Phase 2) */}
            {imageFile && (
              <div style={{ borderTop: `1px solid ${theme.cardBorder}`, paddingTop: '12px', display: 'flex', gap: '8px', flexWrap: 'wrap' }}>
                <button
                  onClick={onRemoveBackground}
                  disabled={isRemovingBackground}
                  style={{
                    flex: 1,
                    minWidth: '120px',
                    padding: '10px',
                    borderRadius: '10px',
                    border: `1px solid ${theme.cardBorder}`,
                    background: isRemovingBackground ? theme.cardBg : `${theme.accent}15`,
                    color: isRemovingBackground ? theme.textMuted : theme.accent,
                    fontSize: '11px',
                    fontWeight: '500',
                    cursor: isRemovingBackground ? 'not-allowed' : 'pointer',
                  }}
                >
                  {isRemovingBackground ? 'Removing...' : 'Remove BG'}
                </button>
                {(subjectImage || imageFile) && generatedBackground && (
                  <button
                    onClick={onAutoLevel}
                    disabled={isAutoLeveling}
                    style={{
                      flex: 1,
                      minWidth: '120px',
                      padding: '10px',
                      borderRadius: '10px',
                      border: `1px solid ${theme.cardBorder}`,
                      background: isAutoLeveling ? theme.cardBg : `${theme.accent}15`,
                      color: isAutoLeveling ? theme.textMuted : theme.accent,
                      fontSize: '11px',
                      fontWeight: '500',
                      cursor: isAutoLeveling ? 'not-allowed' : 'pointer',
                    }}
                  >
                    {isAutoLeveling ? 'Leveling...' : 'Auto-Level'}
                  </button>
                )}
              </div>
            )}
          </div>
        )}

        {/* Video Tab (Phase 3) */}
        {activeTab === 'video' && (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
            <VideoGenerationPanel
              videoModel={videoModel}
              onModelChange={onVideoModelChange}
              videoPrompt={videoPrompt}
              onPromptChange={onVideoPromptChange}
              onGenerate={onGenerateVideo}
              isGenerating={isGeneratingVideo}
              progress={videoGenerationProgress}
              generatedVideoUrl={generatedVideoUrl}
              videoError={videoError}
            />

            {/* Video Overlays - only show after video is generated */}
            {generatedVideoUrl && (
              <div style={{ borderTop: `1px solid ${theme.cardBorder}`, paddingTop: '12px' }}>
                <div
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                    marginBottom: '8px',
                  }}
                >
                  <span style={{ fontSize: '11px', fontWeight: '500', color: theme.textMuted }}>
                    Overlays
                  </span>
                  <button
                    onClick={() => onAddVideoOverlay?.()}
                    style={{
                      padding: '4px 8px',
                      borderRadius: '6px',
                      border: 'none',
                      background: theme.accent,
                      color: '#fff',
                      fontSize: '11px',
                      cursor: 'pointer',
                    }}
                  >
                    + Add Text
                  </button>
                </div>

                {/* Compact overlay list for mobile */}
                {videoOverlays?.slice(0, 2).map((overlay) => (
                  <VideoOverlayEditor
                    key={overlay.id}
                    overlay={overlay}
                    videoDuration={videoDuration || 8}
                    onUpdate={onUpdateVideoOverlay}
                    onRemove={onRemoveVideoOverlay}
                    isSelected={selectedOverlayId === overlay.id}
                    onSelect={onSelectOverlay}
                  />
                ))}

                {videoOverlays?.length > 2 && (
                  <div style={{ fontSize: '11px', color: theme.textMuted, textAlign: 'center', marginTop: '8px' }}>
                    +{videoOverlays.length - 2} more overlays
                  </div>
                )}

                {/* Export button */}
                <button
                  onClick={onOpenVideoExport}
                  disabled={isRenderingVideo}
                  style={{
                    width: '100%',
                    marginTop: '12px',
                    padding: '12px',
                    borderRadius: '10px',
                    border: 'none',
                    background: isRenderingVideo
                      ? theme.cardBg
                      : `linear-gradient(135deg, ${theme.gradientStart}, ${theme.gradientEnd})`,
                    color: isRenderingVideo ? theme.textMuted : '#fff',
                    fontSize: '13px',
                    fontWeight: '600',
                    cursor: isRenderingVideo ? 'not-allowed' : 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    gap: '8px',
                  }}
                >
                  {StudioIcons.download('#fff', 16)} Export Video
                </button>
              </div>
            )}
          </div>
        )}

        {/* Text Tab */}
        {activeTab === 'text' && (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
            <input
              type="text"
              placeholder="Enter text..."
              value={textOverlay?.text || ''}
              onChange={(e) => onTextChange({ text: e.target.value })}
              style={{
                padding: '14px 16px',
                borderRadius: '12px',
                border: `1px solid ${theme.cardBorder}`,
                background: theme.searchBg,
                color: theme.text,
                fontSize: '14px',
                outline: 'none',
              }}
            />
            <div style={{ display: 'flex', gap: '8px', justifyContent: 'center' }}>
              {TEXT_COLORS.map((color) => (
                <button
                  key={color}
                  onClick={() => onTextChange({ color })}
                  style={{
                    width: '36px',
                    height: '36px',
                    borderRadius: '50%',
                    border:
                      textOverlay?.color === color
                        ? `3px solid ${theme.accent}`
                        : `2px solid ${theme.isDark ? 'rgba(255,255,255,0.2)' : 'rgba(0,0,0,0.1)'}`,
                    background: color,
                    cursor: 'pointer',
                  }}
                />
              ))}
            </div>
            <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
              <span style={{ color: theme.textMuted, fontSize: '12px' }}>
                Size: {textOverlay?.fontSize || 48}px
              </span>
              <input
                type="range"
                min="24"
                max="80"
                value={textOverlay?.fontSize || 48}
                onChange={(e) => onTextChange({ fontSize: parseInt(e.target.value) })}
                style={{ flex: 1, accentColor: theme.accent }}
              />
            </div>
            {/* AI Typography Placement (Phase 2) */}
            {textOverlay?.text && (
              <button
                onClick={onSuggestTypography}
                disabled={isSuggestingTypography}
                style={{
                  padding: '10px',
                  borderRadius: '10px',
                  border: `1px solid ${theme.cardBorder}`,
                  background: isSuggestingTypography ? theme.cardBg : `${theme.accent}15`,
                  color: isSuggestingTypography ? theme.textMuted : theme.accent,
                  fontSize: '12px',
                  fontWeight: '500',
                  cursor: isSuggestingTypography ? 'not-allowed' : 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '6px',
                }}
              >
                {isSuggestingTypography ? 'Suggesting...' : <>{StudioIcons.sparkles(theme.accent, 14)} AI Placement</>}
              </button>
            )}
          </div>
        )}

        {/* Templates Tab */}
        {activeTab === 'templates' && (
          <div>
            <div style={{ display: 'flex', gap: '6px', marginBottom: '12px', overflowX: 'auto' }}>
              {templateCategories.slice(0, 4).map((cat) => (
                <button
                  key={cat.id}
                  onClick={() => setTemplateCategory(cat.id)}
                  style={{
                    padding: '5px 12px',
                    borderRadius: '14px',
                    border: 'none',
                    background: templateCategory === cat.id ? theme.accent : theme.cardBg,
                    color: templateCategory === cat.id ? '#fff' : theme.textMuted,
                    fontSize: '11px',
                    fontWeight: '500',
                    cursor: 'pointer',
                    whiteSpace: 'nowrap',
                  }}
                >
                  {cat.label}
                </button>
              ))}
            </div>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '8px' }}>
              {filteredTemplates.slice(0, 6).map((template) => (
                <button
                  key={template.id}
                  onClick={() => onTemplateSelect(template)}
                  style={{
                    padding: '6px',
                    borderRadius: '10px',
                    border:
                      activeTemplateId === template.id
                        ? `2px solid ${theme.accent}`
                        : `1px solid ${theme.cardBorder}`,
                    background:
                      activeTemplateId === template.id ? `${theme.accent}15` : 'transparent',
                    cursor: 'pointer',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    gap: '4px',
                  }}
                >
                  <div
                    style={{
                      width: '100%',
                      height: '40px',
                      borderRadius: '6px',
                      background:
                        template.elements?.[0]?.style === 'gradient'
                          ? `linear-gradient(135deg, ${template.elements[0].colors?.[0]}, ${template.elements[0].colors?.[1]})`
                          : template.elements?.[0]?.color || '#333',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <span style={{ color: '#fff', fontSize: '8px', fontWeight: '700' }}>
                      {template.elements?.find((e) => e.type === 'text')?.content?.slice(0, 8) ||
                        ''}
                    </span>
                  </div>
                  <span style={{ color: theme.text, fontSize: '9px', fontWeight: '500' }}>
                    {template.name}
                  </span>
                </button>
              ))}
            </div>
            {activeTemplateId && (
              <button
                onClick={onClearTemplate}
                style={{
                  width: '100%',
                  marginTop: '8px',
                  padding: '6px',
                  borderRadius: '8px',
                  border: `1px solid ${theme.cardBorder}`,
                  background: 'transparent',
                  color: theme.textMuted,
                  fontSize: '11px',
                  cursor: 'pointer',
                }}
              >
                Clear Template
              </button>
            )}
          </div>
        )}

        {/* Size/Platform Tab */}
        {activeTab === 'size' && (
          <div>
            <div style={{ marginBottom: '12px' }}>
              <span style={{ color: theme.textMuted, fontSize: '12px' }}>
                Current:{' '}
                <span style={{ color: theme.text, fontWeight: '600' }}>
                  {currentPreset?.label || 'Custom'}
                </span>
                <span style={{ marginLeft: '8px', color: theme.accent }}>
                  {exportWidth} × {exportHeight}
                </span>
              </span>
            </div>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '8px' }}>
              {Object.entries(platformPresets)
                .filter(([id]) => id !== 'custom')
                .slice(0, 6)
                .map(([id, preset]) => (
                  <button
                    key={id}
                    onClick={() => onPlatformSelect(id, preset)}
                    style={{
                      padding: '12px 10px',
                      borderRadius: '10px',
                      border:
                        selectedPlatform === id
                          ? `2px solid ${theme.accent}`
                          : `1px solid ${theme.cardBorder}`,
                      background: selectedPlatform === id ? `${theme.accent}15` : 'transparent',
                      cursor: 'pointer',
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'flex-start',
                      gap: '4px',
                    }}
                  >
                    <span
                      style={{
                        color: selectedPlatform === id ? theme.accent : theme.text,
                        fontSize: '12px',
                        fontWeight: '600',
                      }}
                    >
                      {preset.label}
                    </span>
                    <span style={{ color: theme.textMuted, fontSize: '10px' }}>
                      {preset.aspectRatio}
                    </span>
                  </button>
                ))}
            </div>
          </div>
        )}
      </div>

      {/* Tab Bar */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-around',
          padding: '8px 0',
          paddingBottom: 'env(safe-area-inset-bottom, 8px)',
        }}
      >
        {tabs.map((tab) => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: '4px',
              padding: '8px 12px',
              background: 'none',
              border: 'none',
              cursor: 'pointer',
              opacity: activeTab === tab.id ? 1 : 0.5,
              minWidth: '64px',
            }}
          >
            <div
              style={{
                width: '32px',
                height: '32px',
                borderRadius: '8px',
                background: activeTab === tab.id ? `${theme.accent}22` : 'transparent',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
              }}
            >
              {tab.icon(activeTab === tab.id ? theme.accent : theme.textMuted, 20)}
            </div>
            <span
              style={{
                fontSize: '10px',
                fontWeight: activeTab === tab.id ? '600' : '400',
                color: activeTab === tab.id ? theme.accent : theme.textMuted,
              }}
            >
              {tab.label}
            </span>
          </button>
        ))}
      </div>
    </div>
  )
}

export default StudioMobileControls

import { usePhoneTheme } from '@/context/PhoneThemeContext'
import { gradientPresets } from '@/lib/platformTemplates'
import { StudioIcons } from '../utils/StudioIcons'
import { SOLID_COLORS, TEXT_COLORS } from '../utils/studioConstants'
import { VideoGenerationPanel } from './VideoGenerationPanel'
import { VideoOverlayEditor } from './VideoOverlayEditor'

/**
 * StudioSidebar - Desktop left sidebar with all studio controls
 *
 * Sections:
 * 1. Upload Media
 * 2. AI Magic
 * 3. Templates
 * 4. Background Colors
 * 5. Text Overlay
 */
export function StudioSidebar({
  // Upload
  imageFile,
  onImageUpload,

  // AI - Copy generation
  prompt,
  onPromptChange,
  onGenerate,
  isGenerating,
  onAnalyzeImage,
  isAnalyzing,

  // AI - Background generation (Phase 2)
  backgroundPrompt,
  onBackgroundPromptChange,
  onGenerateBackground,
  isGeneratingBackground,
  generatedBackground,

  // AI - Subject processing (Phase 2)
  onRemoveBackground,
  isRemovingBackground,
  subjectImage,

  // AI - Typography (Phase 2)
  onSuggestTypography,
  isSuggestingTypography,

  // AI - Auto-level (Phase 2)
  onAutoLevel,
  isAutoLeveling,

  // AI - Video generation (Phase 3)
  videoModel,
  onVideoModelChange,
  videoPrompt,
  onVideoPromptChange,
  onGenerateVideo,
  isGeneratingVideo,
  videoGenerationProgress,
  generatedVideoUrl,
  videoError,
  // Video overlays (Phase 3)
  videoOverlays,
  selectedOverlayId,
  onSelectOverlay,
  onAddVideoOverlay,
  onUpdateVideoOverlay,
  onRemoveVideoOverlay,
  videoDuration,
  // Video rendering (Phase 3)
  onRenderFinalVideo,
  isRenderingVideo,
  onOpenVideoExport,

  // Templates
  templates = [],
  activeTemplateId,
  onTemplateSelect,
  onClearTemplate,
  onOpenTemplateLibrary,

  // Background
  background,
  onBackgroundChange,

  // Text
  textOverlay,
  onTextChange,
}) {
  const { theme } = usePhoneTheme()

  return (
    <div
      style={{
        width: '320px',
        borderRight: `1px solid ${theme.cardBorder}`,
        background: theme.navBg,
        backdropFilter: 'blur(20px)',
        padding: '20px',
        overflowY: 'auto',
        display: 'flex',
        flexDirection: 'column',
        gap: '24px',
      }}
    >
      {/* Upload Media Section */}
      <div>
        <h3
          style={{
            color: theme.text,
            fontSize: '14px',
            fontWeight: '600',
            marginBottom: '12px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
          }}
        >
          {StudioIcons.image(theme.accent, 18)} Upload Media
        </h3>
        <label
          style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: '12px',
            padding: '32px',
            borderRadius: '16px',
            border: `2px dashed ${theme.cardBorder}`,
            background: theme.cardBg,
            cursor: 'pointer',
          }}
        >
          <input
            type="file"
            accept="image/*,video/*"
            onChange={(e) => e.target.files?.[0] && onImageUpload(e.target.files[0])}
            style={{ display: 'none' }}
          />
          <div
            style={{
              width: '56px',
              height: '56px',
              borderRadius: '50%',
              background: `linear-gradient(135deg, ${theme.gradientStart}33, ${theme.gradientEnd}33)`,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            {StudioIcons.upload(theme.accent)}
          </div>
          <div style={{ textAlign: 'center' }}>
            <p style={{ color: theme.text, fontSize: '14px', fontWeight: '600', margin: 0 }}>
              {imageFile ? imageFile.name : 'Drop your image here'}
            </p>
            <p style={{ color: theme.textMuted, fontSize: '12px', margin: '4px 0 0' }}>
              PNG, JPG up to 10MB
            </p>
          </div>
        </label>
      </div>

      {/* AI Magic Section */}
      <div>
        <h3
          style={{
            color: theme.text,
            fontSize: '14px',
            fontWeight: '600',
            marginBottom: '12px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
          }}
        >
          {StudioIcons.sparkles(theme.accent, 18)} AI Magic
        </h3>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
          <input
            type="text"
            placeholder="Describe your marketing content..."
            value={prompt}
            onChange={(e) => onPromptChange(e.target.value)}
            style={{
              padding: '14px 16px',
              borderRadius: '12px',
              border: `1px solid ${theme.cardBorder}`,
              background: theme.searchBg,
              color: theme.text,
              fontSize: '14px',
              outline: 'none',
            }}
          />
          <button
            onClick={onGenerate}
            disabled={isGenerating || !prompt}
            style={{
              padding: '14px',
              borderRadius: '12px',
              border: 'none',
              background:
                isGenerating || !prompt
                  ? theme.cardBg
                  : `linear-gradient(135deg, ${theme.gradientStart}, ${theme.gradientEnd})`,
              color: isGenerating || !prompt ? theme.textMuted : '#fff',
              fontSize: '14px',
              fontWeight: '600',
              cursor: isGenerating || !prompt ? 'not-allowed' : 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px',
            }}
          >
            {isGenerating ? (
              <>
                <div
                  style={{
                    width: '18px',
                    height: '18px',
                    border: `2px solid ${theme.textMuted}`,
                    borderTopColor: 'transparent',
                    borderRadius: '50%',
                    animation: 'spin 1s linear infinite',
                  }}
                />
                Generating...
              </>
            ) : (
              <>{StudioIcons.sparkles('#fff', 18)} Generate with AI</>
            )}
          </button>

          {/* Analyze Image Button - only show when image is uploaded */}
          {imageFile && (
            <button
              onClick={onAnalyzeImage}
              disabled={isAnalyzing}
              style={{
                padding: '12px',
                borderRadius: '12px',
                border: `1px solid ${theme.cardBorder}`,
                background: isAnalyzing ? theme.cardBg : `${theme.accent}15`,
                color: isAnalyzing ? theme.textMuted : theme.accent,
                fontSize: '13px',
                fontWeight: '500',
                cursor: isAnalyzing ? 'not-allowed' : 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px',
              }}
            >
              {isAnalyzing ? (
                <>
                  <div
                    style={{
                      width: '16px',
                      height: '16px',
                      border: `2px solid ${theme.textMuted}`,
                      borderTopColor: 'transparent',
                      borderRadius: '50%',
                      animation: 'spin 1s linear infinite',
                    }}
                  />
                  Analyzing Image...
                </>
              ) : (
                <>{StudioIcons.image(theme.accent, 16)} Analyze Image for Copy</>
              )}
            </button>
          )}
        </div>
      </div>

      {/* AI Background Generation Section (Phase 2) */}
      <div>
        <h3
          style={{
            color: theme.text,
            fontSize: '14px',
            fontWeight: '600',
            marginBottom: '12px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
          }}
        >
          {StudioIcons.layers(theme.accent, 18)} AI Background
        </h3>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
          <input
            type="text"
            placeholder="Describe background... (marble desk, beach sunset)"
            value={backgroundPrompt || ''}
            onChange={(e) => onBackgroundPromptChange?.(e.target.value)}
            style={{
              padding: '14px 16px',
              borderRadius: '12px',
              border: `1px solid ${theme.cardBorder}`,
              background: theme.searchBg,
              color: theme.text,
              fontSize: '14px',
              outline: 'none',
            }}
          />
          <button
            onClick={onGenerateBackground}
            disabled={isGeneratingBackground || !backgroundPrompt}
            style={{
              padding: '14px',
              borderRadius: '12px',
              border: 'none',
              background:
                isGeneratingBackground || !backgroundPrompt
                  ? theme.cardBg
                  : `linear-gradient(135deg, ${theme.gradientStart}, ${theme.gradientEnd})`,
              color: isGeneratingBackground || !backgroundPrompt ? theme.textMuted : '#fff',
              fontSize: '14px',
              fontWeight: '600',
              cursor: isGeneratingBackground || !backgroundPrompt ? 'not-allowed' : 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px',
            }}
          >
            {isGeneratingBackground ? (
              <>
                <div
                  style={{
                    width: '18px',
                    height: '18px',
                    border: `2px solid ${theme.textMuted}`,
                    borderTopColor: 'transparent',
                    borderRadius: '50%',
                    animation: 'spin 1s linear infinite',
                  }}
                />
                Generating Background...
              </>
            ) : (
              <>{StudioIcons.sparkles('#fff', 18)} Generate Background</>
            )}
          </button>
          {generatedBackground && (
            <div
              style={{
                padding: '8px',
                borderRadius: '8px',
                background: `${theme.accent}15`,
                color: theme.accent,
                fontSize: '12px',
                textAlign: 'center',
              }}
            >
              AI background applied
            </div>
          )}
        </div>
      </div>

      {/* Subject Processing Section (Phase 2) */}
      {imageFile && (
        <div>
          <h3
            style={{
              color: theme.text,
              fontSize: '14px',
              fontWeight: '600',
              marginBottom: '12px',
              display: 'flex',
              alignItems: 'center',
              gap: '8px',
            }}
          >
            {StudioIcons.image(theme.accent, 18)} Subject Processing
          </h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
            {/* Remove Background Button */}
            <button
              onClick={onRemoveBackground}
              disabled={isRemovingBackground}
              style={{
                padding: '12px',
                borderRadius: '12px',
                border: `1px solid ${theme.cardBorder}`,
                background: isRemovingBackground ? theme.cardBg : `${theme.accent}15`,
                color: isRemovingBackground ? theme.textMuted : theme.accent,
                fontSize: '13px',
                fontWeight: '500',
                cursor: isRemovingBackground ? 'not-allowed' : 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px',
              }}
            >
              {isRemovingBackground ? (
                <>
                  <div
                    style={{
                      width: '16px',
                      height: '16px',
                      border: `2px solid ${theme.textMuted}`,
                      borderTopColor: 'transparent',
                      borderRadius: '50%',
                      animation: 'spin 1s linear infinite',
                    }}
                  />
                  Removing Background...
                </>
              ) : (
                <>{StudioIcons.layers(theme.accent, 16)} Remove Background</>
              )}
            </button>

            {/* Auto-Level Button - only show when both subject and generated background exist */}
            {(subjectImage || imageFile) && generatedBackground && (
              <button
                onClick={onAutoLevel}
                disabled={isAutoLeveling}
                style={{
                  padding: '12px',
                  borderRadius: '12px',
                  border: `1px solid ${theme.cardBorder}`,
                  background: isAutoLeveling ? theme.cardBg : `${theme.accent}15`,
                  color: isAutoLeveling ? theme.textMuted : theme.accent,
                  fontSize: '13px',
                  fontWeight: '500',
                  cursor: isAutoLeveling ? 'not-allowed' : 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  gap: '8px',
                }}
              >
                {isAutoLeveling ? (
                  <>
                    <div
                      style={{
                        width: '16px',
                        height: '16px',
                        border: `2px solid ${theme.textMuted}`,
                        borderTopColor: 'transparent',
                        borderRadius: '50%',
                        animation: 'spin 1s linear infinite',
                      }}
                    />
                    Auto-Leveling...
                  </>
                ) : (
                  <>{StudioIcons.sparkles(theme.accent, 16)} Auto-Level Colors</>
                )}
              </button>
            )}

            {subjectImage && (
              <div
                style={{
                  padding: '8px',
                  borderRadius: '8px',
                  background: `${theme.accent}15`,
                  color: theme.accent,
                  fontSize: '12px',
                  textAlign: 'center',
                }}
              >
                Background removed
              </div>
            )}
          </div>
        </div>
      )}

      {/* AI Video Generation Section (Phase 3) */}
      <div>
        <h3
          style={{
            color: theme.text,
            fontSize: '14px',
            fontWeight: '600',
            marginBottom: '12px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
          }}
        >
          {StudioIcons.video(theme.accent, 18)} AI Video
        </h3>

        <VideoGenerationPanel
          videoModel={videoModel}
          onModelChange={onVideoModelChange}
          videoPrompt={videoPrompt}
          onPromptChange={onVideoPromptChange}
          onGenerate={onGenerateVideo}
          isGenerating={isGeneratingVideo}
          progress={videoGenerationProgress}
          generatedVideoUrl={generatedVideoUrl}
          videoError={videoError}
        />

        {/* Video Overlays - only show after video is generated */}
        {generatedVideoUrl && (
          <div style={{ marginTop: '16px' }}>
            <div
              style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between',
                marginBottom: '8px',
              }}
            >
              <span style={{ fontSize: '12px', fontWeight: '500', color: theme.textSecondary }}>
                Video Overlays
              </span>
              <button
                onClick={() => onAddVideoOverlay?.()}
                style={{
                  padding: '4px 10px',
                  borderRadius: '6px',
                  border: 'none',
                  background: theme.accent,
                  color: '#fff',
                  fontSize: '12px',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '4px',
                }}
              >
                {StudioIcons.plus('#fff', 14)} Add Text
              </button>
            </div>

            {/* Overlay list */}
            <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
              {videoOverlays?.map((overlay) => (
                <VideoOverlayEditor
                  key={overlay.id}
                  overlay={overlay}
                  videoDuration={videoDuration || 8}
                  onUpdate={onUpdateVideoOverlay}
                  onRemove={onRemoveVideoOverlay}
                  isSelected={selectedOverlayId === overlay.id}
                  onSelect={onSelectOverlay}
                />
              ))}
            </div>

            {/* Export button */}
            <button
              onClick={onOpenVideoExport}
              disabled={isRenderingVideo}
              style={{
                width: '100%',
                marginTop: '12px',
                padding: '12px',
                borderRadius: '10px',
                border: 'none',
                background: isRenderingVideo
                  ? theme.cardBg
                  : `linear-gradient(135deg, ${theme.gradientStart}, ${theme.gradientEnd})`,
                color: isRenderingVideo ? theme.textMuted : '#fff',
                fontSize: '14px',
                fontWeight: '600',
                cursor: isRenderingVideo ? 'not-allowed' : 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px',
              }}
            >
              {StudioIcons.download('#fff', 18)} Export Video
            </button>
          </div>
        )}
      </div>

      {/* Templates Section */}
      <div>
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            marginBottom: '12px',
          }}
        >
          <h3
            style={{
              color: theme.text,
              fontSize: '14px',
              fontWeight: '600',
              margin: 0,
              display: 'flex',
              alignItems: 'center',
              gap: '8px',
            }}
          >
            {StudioIcons.grid(theme.accent, 18)} Templates
          </h3>
          <button
            onClick={onOpenTemplateLibrary}
            style={{
              background: 'none',
              border: 'none',
              color: theme.accent,
              fontSize: '12px',
              fontWeight: '500',
              cursor: 'pointer',
            }}
          >
            See all
          </button>
        </div>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '8px' }}>
          {templates.slice(0, 4).map((template) => (
            <button
              key={template.id}
              onClick={() => onTemplateSelect(template)}
              style={{
                padding: '12px 8px',
                borderRadius: '12px',
                border:
                  activeTemplateId === template.id
                    ? `2px solid ${theme.accent}`
                    : `1px solid ${theme.cardBorder}`,
                background:
                  activeTemplateId === template.id ? `${theme.accent}15` : 'transparent',
                cursor: 'pointer',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                gap: '6px',
              }}
            >
              {/* Template Preview */}
              <div
                style={{
                  width: '100%',
                  height: '48px',
                  borderRadius: '8px',
                  background:
                    template.elements?.[0]?.style === 'gradient'
                      ? `linear-gradient(135deg, ${template.elements[0].colors?.[0]}, ${template.elements[0].colors?.[1]})`
                      : template.elements?.[0]?.color || '#333',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  overflow: 'hidden',
                }}
              >
                <span
                  style={{
                    color: '#fff',
                    fontSize: '10px',
                    fontWeight: '700',
                    textAlign: 'center',
                    padding: '4px',
                  }}
                >
                  {template.elements?.find((e) => e.type === 'text')?.content?.slice(0, 12) ||
                    template.name}
                </span>
              </div>
              <span style={{ color: theme.text, fontSize: '11px', fontWeight: '500' }}>
                {template.name}
              </span>
            </button>
          ))}
        </div>
        {activeTemplateId && (
          <button
            onClick={onClearTemplate}
            style={{
              width: '100%',
              marginTop: '8px',
              padding: '8px',
              borderRadius: '8px',
              border: `1px solid ${theme.cardBorder}`,
              background: 'transparent',
              color: theme.textMuted,
              fontSize: '12px',
              cursor: 'pointer',
            }}
          >
            Clear Template
          </button>
        )}
      </div>

      {/* Background Colors Section */}
      <div>
        <h3
          style={{
            color: theme.text,
            fontSize: '14px',
            fontWeight: '600',
            marginBottom: '12px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
          }}
        >
          {StudioIcons.layers(theme.accent, 18)} Background
        </h3>

        {/* Solid Colors */}
        <div style={{ marginBottom: '12px' }}>
          <span
            style={{
              color: theme.textMuted,
              fontSize: '12px',
              marginBottom: '8px',
              display: 'block',
            }}
          >
            Solid Colors
          </span>
          <div style={{ display: 'flex', gap: '6px', flexWrap: 'wrap' }}>
            {SOLID_COLORS.map((color) => (
              <button
                key={color}
                onClick={() => onBackgroundChange({ type: 'solid', value: color })}
                style={{
                  width: '28px',
                  height: '28px',
                  borderRadius: '6px',
                  border:
                    background?.value === color
                      ? `2px solid ${theme.accent}`
                      : `1px solid ${theme.cardBorder}`,
                  background: color,
                  cursor: 'pointer',
                }}
              />
            ))}
          </div>
        </div>

        {/* Gradients */}
        <div>
          <span
            style={{
              color: theme.textMuted,
              fontSize: '12px',
              marginBottom: '8px',
              display: 'block',
            }}
          >
            Gradients
          </span>
          <div style={{ display: 'flex', gap: '6px', flexWrap: 'wrap' }}>
            {gradientPresets.slice(0, 6).map((gradient) => (
              <button
                key={gradient.id}
                onClick={() => onBackgroundChange({ type: 'gradient', value: gradient.colors })}
                title={gradient.label}
                style={{
                  width: '28px',
                  height: '28px',
                  borderRadius: '6px',
                  border:
                    JSON.stringify(background?.value) === JSON.stringify(gradient.colors)
                      ? `2px solid ${theme.accent}`
                      : `1px solid ${theme.cardBorder}`,
                  background: `linear-gradient(135deg, ${gradient.colors[0]}, ${gradient.colors[1]})`,
                  cursor: 'pointer',
                }}
              />
            ))}
          </div>
        </div>
      </div>

      {/* Text Overlay Section */}
      <div>
        <h3
          style={{
            color: theme.text,
            fontSize: '14px',
            fontWeight: '600',
            marginBottom: '12px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
          }}
        >
          {StudioIcons.type(theme.accent, 18)} Text Overlay
        </h3>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
          {/* Text Input */}
          <input
            type="text"
            placeholder="Enter text..."
            value={textOverlay?.text || ''}
            onChange={(e) => onTextChange({ text: e.target.value })}
            style={{
              padding: '14px 16px',
              borderRadius: '12px',
              border: `1px solid ${theme.cardBorder}`,
              background: theme.searchBg,
              color: theme.text,
              fontSize: '14px',
              outline: 'none',
            }}
          />

          {/* Text Color */}
          <div>
            <span
              style={{
                color: theme.textMuted,
                fontSize: '12px',
                marginBottom: '8px',
                display: 'block',
              }}
            >
              Text Color
            </span>
            <div style={{ display: 'flex', gap: '8px' }}>
              {TEXT_COLORS.map((color) => (
                <button
                  key={color}
                  onClick={() => onTextChange({ color })}
                  style={{
                    width: '32px',
                    height: '32px',
                    borderRadius: '50%',
                    border:
                      textOverlay?.color === color
                        ? `3px solid ${theme.accent}`
                        : `2px solid ${theme.isDark ? 'rgba(255,255,255,0.2)' : 'rgba(0,0,0,0.1)'}`,
                    background: color,
                    cursor: 'pointer',
                    boxShadow:
                      textOverlay?.color === color ? `0 0 12px ${theme.accentGlow}` : 'none',
                  }}
                />
              ))}
            </div>
          </div>

          {/* Font Size */}
          <div>
            <span
              style={{
                color: theme.textMuted,
                fontSize: '12px',
                marginBottom: '8px',
                display: 'flex',
                justifyContent: 'space-between',
              }}
            >
              Font Size{' '}
              <span style={{ color: theme.text }}>{textOverlay?.fontSize || 48}px</span>
            </span>
            <input
              type="range"
              min="24"
              max="120"
              value={textOverlay?.fontSize || 48}
              onChange={(e) => onTextChange({ fontSize: parseInt(e.target.value) })}
              style={{ width: '100%', accentColor: theme.accent }}
            />
          </div>

          {/* AI Typography Placement (Phase 2) */}
          {textOverlay?.text && (
            <button
              onClick={onSuggestTypography}
              disabled={isSuggestingTypography}
              style={{
                padding: '12px',
                borderRadius: '12px',
                border: `1px solid ${theme.cardBorder}`,
                background: isSuggestingTypography ? theme.cardBg : `${theme.accent}15`,
                color: isSuggestingTypography ? theme.textMuted : theme.accent,
                fontSize: '13px',
                fontWeight: '500',
                cursor: isSuggestingTypography ? 'not-allowed' : 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px',
              }}
            >
              {isSuggestingTypography ? (
                <>
                  <div
                    style={{
                      width: '16px',
                      height: '16px',
                      border: `2px solid ${theme.textMuted}`,
                      borderTopColor: 'transparent',
                      borderRadius: '50%',
                      animation: 'spin 1s linear infinite',
                    }}
                  />
                  Suggesting Placement...
                </>
              ) : (
                <>{StudioIcons.sparkles(theme.accent, 16)} AI Typography Placement</>
              )}
            </button>
          )}
        </div>
      </div>

      {/* Keyframe for spinner */}
      <style>{`@keyframes spin { from { transform: rotate(0deg); } to { transform: rotate(360deg); } }`}</style>
    </div>
  )
}

export default StudioSidebar

/**
 * VideoGenerationPanel - Controls for AI video generation
 * Includes model selector, prompt input, and generate button with progress
 */

import { usePhoneTheme } from '@/context/PhoneThemeContext'
import { VideoModelSelector } from './VideoModelSelector'
import { StudioIcons } from '../utils/StudioIcons'

export function VideoGenerationPanel({
  // Model selection
  videoModel,
  onModelChange,
  // Prompt
  videoPrompt,
  onPromptChange,
  // Generation
  onGenerate,
  isGenerating,
  progress,
  // Result
  generatedVideoUrl,
  videoError,
}) {
  const { theme } = usePhoneTheme()

  const canGenerate = videoPrompt?.trim() && !isGenerating

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
      {/* Model Selector */}
      <VideoModelSelector
        selectedModel={videoModel}
        onModelChange={onModelChange}
        disabled={isGenerating}
      />

      {/* Prompt Input */}
      <div>
        <label
          style={{
            display: 'block',
            fontSize: '12px',
            fontWeight: '500',
            color: theme.textSecondary,
            marginBottom: '6px',
          }}
        >
          Describe your video
        </label>
        <textarea
          value={videoPrompt}
          onChange={(e) => onPromptChange(e.target.value)}
          placeholder="e.g., A sleek smartphone floating over a gradient background with soft particles..."
          disabled={isGenerating}
          style={{
            width: '100%',
            minHeight: '80px',
            padding: '10px 12px',
            borderRadius: '8px',
            border: `1px solid ${theme.border}`,
            background: theme.bg,
            color: theme.text,
            fontSize: '13px',
            fontFamily: 'inherit',
            resize: 'vertical',
            outline: 'none',
            opacity: isGenerating ? 0.5 : 1,
          }}
        />
      </div>

      {/* Generate Button */}
      <button
        onClick={onGenerate}
        disabled={!canGenerate}
        style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: '8px',
          padding: '12px 16px',
          borderRadius: '8px',
          border: 'none',
          background: canGenerate ? theme.accent : theme.bgSecondary,
          color: canGenerate ? '#fff' : theme.textSecondary,
          cursor: canGenerate ? 'pointer' : 'not-allowed',
          fontWeight: '600',
          fontSize: '14px',
          transition: 'all 0.2s ease',
          position: 'relative',
          overflow: 'hidden',
        }}
      >
        {isGenerating ? (
          <>
            {/* Progress ring */}
            <div
              style={{
                width: '18px',
                height: '18px',
                borderRadius: '50%',
                border: '2px solid rgba(255,255,255,0.3)',
                borderTopColor: '#fff',
                animation: 'spin 1s linear infinite',
              }}
            />
            <span>Generating... {Math.round(progress)}%</span>
          </>
        ) : (
          <>
            {StudioIcons.video('#fff', 18)}
            <span>Generate Video</span>
          </>
        )}

        {/* Progress bar background */}
        {isGenerating && (
          <div
            style={{
              position: 'absolute',
              left: 0,
              bottom: 0,
              height: '3px',
              width: `${progress}%`,
              background: 'rgba(255,255,255,0.5)',
              transition: 'width 0.3s ease',
            }}
          />
        )}
      </button>

      {/* Error message */}
      {videoError && (
        <div
          style={{
            padding: '10px 12px',
            borderRadius: '8px',
            background: '#ef444415',
            border: '1px solid #ef4444',
            color: '#ef4444',
            fontSize: '12px',
          }}
        >
          {videoError}
        </div>
      )}

      {/* Success indicator */}
      {generatedVideoUrl && !isGenerating && (
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            padding: '10px 12px',
            borderRadius: '8px',
            background: '#22c55e15',
            border: '1px solid #22c55e',
            color: '#22c55e',
            fontSize: '12px',
          }}
        >
          {StudioIcons.check('#22c55e', 16)}
          <span>Video generated successfully!</span>
        </div>
      )}

      {/* CSS for spinner animation */}
      <style>{`
        @keyframes spin {
          to { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  )
}

export default VideoGenerationPanel

/**
 * VideoModelSelector - Toggle between Veo 3.1 and Sora 2 video generation models
 */

import { usePhoneTheme } from '@/context/PhoneThemeContext'
import { VIDEO_MODELS } from '../utils/videoConstants'

export function VideoModelSelector({ selectedModel, onModelChange, disabled = false }) {
  const { theme } = usePhoneTheme()

  const models = Object.values(VIDEO_MODELS)

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
      <div
        style={{
          display: 'flex',
          gap: '8px',
          background: theme.bgSecondary,
          borderRadius: '8px',
          padding: '4px',
        }}
      >
        {models.map((model) => {
          const isSelected = selectedModel === model.id
          return (
            <button
              key={model.id}
              onClick={() => !disabled && onModelChange(model.id)}
              disabled={disabled}
              style={{
                flex: 1,
                padding: '10px 12px',
                borderRadius: '6px',
                border: 'none',
                cursor: disabled ? 'not-allowed' : 'pointer',
                background: isSelected ? theme.accent : 'transparent',
                color: isSelected ? '#fff' : theme.textSecondary,
                fontWeight: isSelected ? '600' : '400',
                fontSize: '13px',
                transition: 'all 0.2s ease',
                opacity: disabled ? 0.5 : 1,
              }}
            >
              <div style={{ fontWeight: '600' }}>{model.name}</div>
              <div
                style={{
                  fontSize: '10px',
                  opacity: 0.8,
                  marginTop: '2px',
                }}
              >
                {model.provider}
              </div>
            </button>
          )
        })}
      </div>

      {/* Model description */}
      {selectedModel && VIDEO_MODELS[selectedModel] && (
        <div
          style={{
            fontSize: '11px',
            color: theme.textSecondary,
            padding: '8px 12px',
            background: `${theme.accent}15`,
            borderRadius: '6px',
            lineHeight: '1.4',
          }}
        >
          <div style={{ fontWeight: '500', marginBottom: '4px' }}>
            {VIDEO_MODELS[selectedModel].description}
          </div>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '4px', marginTop: '6px' }}>
            {VIDEO_MODELS[selectedModel].features.map((feature, i) => (
              <span
                key={i}
                style={{
                  background: theme.accent,
                  color: '#fff',
                  padding: '2px 6px',
                  borderRadius: '4px',
                  fontSize: '10px',
                }}
              >
                {feature}
              </span>
            ))}
          </div>
        </div>
      )}
    </div>
  )
}

export default VideoModelSelector

/**
 * VideoOverlayEditor - Edit overlay properties (content, position, timing, animation, style)
 */

import { usePhoneTheme } from '@/context/PhoneThemeContext'
import { StudioIcons } from '../utils/StudioIcons'
import { OVERLAY_ANIMATIONS, FONT_SIZE_PRESETS, FONT_WEIGHT_PRESETS } from '../utils/videoConstants'

// Color palette for overlay text
const COLOR_PALETTE = [
  '#ffffff',
  '#000000',
  '#ef4444',
  '#f59e0b',
  '#22c55e',
  '#3b82f6',
  '#a855f7',
  '#ec4899',
]

export function VideoOverlayEditor({
  overlay,
  videoDuration,
  onUpdate,
  onRemove,
  isSelected,
  onSelect,
}) {
  const { theme } = usePhoneTheme()

  if (!overlay) return null

  const handleChange = (field, value) => {
    if (field.includes('.')) {
      // Handle nested fields like 'style.color' or 'timing.start'
      const [parent, child] = field.split('.')
      onUpdate(overlay.id, {
        [parent]: {
          ...overlay[parent],
          [child]: value,
        },
      })
    } else {
      onUpdate(overlay.id, { [field]: value })
    }
  }

  return (
    <div
      onClick={() => onSelect?.(overlay.id)}
      style={{
        padding: '12px',
        borderRadius: '8px',
        background: isSelected ? `${theme.accent}15` : theme.bgSecondary,
        border: `1px solid ${isSelected ? theme.accent : theme.border}`,
        transition: 'all 0.2s ease',
        cursor: 'pointer',
      }}
    >
      {/* Header */}
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          marginBottom: '12px',
        }}
      >
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          {StudioIcons.type(theme.accent, 16)}
          <span style={{ fontSize: '13px', fontWeight: '600', color: theme.text }}>
            {overlay.type === 'text' ? 'Text Overlay' : 'Image Overlay'}
          </span>
        </div>
        <button
          onClick={(e) => {
            e.stopPropagation()
            onRemove(overlay.id)
          }}
          style={{
            padding: '4px',
            background: 'transparent',
            border: 'none',
            cursor: 'pointer',
            opacity: 0.6,
            transition: 'opacity 0.2s',
          }}
          onMouseEnter={(e) => (e.currentTarget.style.opacity = 1)}
          onMouseLeave={(e) => (e.currentTarget.style.opacity = 0.6)}
        >
          {StudioIcons.trash('#ef4444', 16)}
        </button>
      </div>

      {/* Content input (for text) */}
      {overlay.type === 'text' && (
        <div style={{ marginBottom: '12px' }}>
          <label style={labelStyle(theme)}>Content</label>
          <input
            type="text"
            value={overlay.content}
            onChange={(e) => handleChange('content', e.target.value)}
            onClick={(e) => e.stopPropagation()}
            style={inputStyle(theme)}
            placeholder="Enter text..."
          />
        </div>
      )}

      {/* Position sliders */}
      <div style={{ marginBottom: '12px' }}>
        <label style={labelStyle(theme)}>Position</label>
        <div style={{ display: 'flex', gap: '12px' }}>
          <div style={{ flex: 1 }}>
            <div style={{ fontSize: '10px', color: theme.textSecondary, marginBottom: '4px' }}>
              X: {Math.round(overlay.position.x)}%
            </div>
            <input
              type="range"
              min={0}
              max={100}
              value={overlay.position.x}
              onChange={(e) => handleChange('position.x', parseFloat(e.target.value))}
              onClick={(e) => e.stopPropagation()}
              style={sliderStyle(theme)}
            />
          </div>
          <div style={{ flex: 1 }}>
            <div style={{ fontSize: '10px', color: theme.textSecondary, marginBottom: '4px' }}>
              Y: {Math.round(overlay.position.y)}%
            </div>
            <input
              type="range"
              min={0}
              max={100}
              value={overlay.position.y}
              onChange={(e) => handleChange('position.y', parseFloat(e.target.value))}
              onClick={(e) => e.stopPropagation()}
              style={sliderStyle(theme)}
            />
          </div>
        </div>
      </div>

      {/* Timing sliders */}
      <div style={{ marginBottom: '12px' }}>
        <label style={labelStyle(theme)}>
          {StudioIcons.clock(theme.textSecondary, 12)} Timing
        </label>
        <div style={{ display: 'flex', gap: '12px' }}>
          <div style={{ flex: 1 }}>
            <div style={{ fontSize: '10px', color: theme.textSecondary, marginBottom: '4px' }}>
              Start: {overlay.timing.start.toFixed(1)}s
            </div>
            <input
              type="range"
              min={0}
              max={videoDuration}
              step={0.1}
              value={overlay.timing.start}
              onChange={(e) => handleChange('timing.start', parseFloat(e.target.value))}
              onClick={(e) => e.stopPropagation()}
              style={sliderStyle(theme)}
            />
          </div>
          <div style={{ flex: 1 }}>
            <div style={{ fontSize: '10px', color: theme.textSecondary, marginBottom: '4px' }}>
              End: {overlay.timing.end.toFixed(1)}s
            </div>
            <input
              type="range"
              min={0}
              max={videoDuration}
              step={0.1}
              value={overlay.timing.end}
              onChange={(e) => handleChange('timing.end', parseFloat(e.target.value))}
              onClick={(e) => e.stopPropagation()}
              style={sliderStyle(theme)}
            />
          </div>
        </div>
      </div>

      {/* Animation dropdown */}
      <div style={{ marginBottom: '12px' }}>
        <label style={labelStyle(theme)}>Animation</label>
        <select
          value={overlay.animation}
          onChange={(e) => handleChange('animation', e.target.value)}
          onClick={(e) => e.stopPropagation()}
          style={selectStyle(theme)}
        >
          {OVERLAY_ANIMATIONS.map((anim) => (
            <option key={anim.id} value={anim.id}>
              {anim.name}
            </option>
          ))}
        </select>
      </div>

      {/* Style controls (for text) */}
      {overlay.type === 'text' && (
        <>
          {/* Color picker */}
          <div style={{ marginBottom: '12px' }}>
            <label style={labelStyle(theme)}>Color</label>
            <div style={{ display: 'flex', gap: '6px', flexWrap: 'wrap' }}>
              {COLOR_PALETTE.map((color) => (
                <button
                  key={color}
                  onClick={(e) => {
                    e.stopPropagation()
                    handleChange('style.color', color)
                  }}
                  style={{
                    width: '24px',
                    height: '24px',
                    borderRadius: '4px',
                    border:
                      overlay.style?.color === color
                        ? `2px solid ${theme.accent}`
                        : '2px solid transparent',
                    background: color,
                    cursor: 'pointer',
                    boxShadow: color === '#ffffff' ? 'inset 0 0 0 1px rgba(0,0,0,0.1)' : 'none',
                  }}
                />
              ))}
            </div>
          </div>

          {/* Font size */}
          <div style={{ marginBottom: '12px' }}>
            <label style={labelStyle(theme)}>Font Size</label>
            <div style={{ display: 'flex', gap: '6px' }}>
              {FONT_SIZE_PRESETS.map((preset) => (
                <button
                  key={preset.id}
                  onClick={(e) => {
                    e.stopPropagation()
                    handleChange('style.fontSize', preset.value)
                  }}
                  style={{
                    flex: 1,
                    padding: '6px 8px',
                    borderRadius: '4px',
                    border: 'none',
                    background:
                      overlay.style?.fontSize === preset.value ? theme.accent : theme.bg,
                    color: overlay.style?.fontSize === preset.value ? '#fff' : theme.text,
                    fontSize: '11px',
                    cursor: 'pointer',
                  }}
                >
                  {preset.name}
                </button>
              ))}
            </div>
          </div>

          {/* Font weight */}
          <div style={{ marginBottom: '12px' }}>
            <label style={labelStyle(theme)}>Font Weight</label>
            <select
              value={overlay.style?.fontWeight || 'bold'}
              onChange={(e) => handleChange('style.fontWeight', e.target.value)}
              onClick={(e) => e.stopPropagation()}
              style={selectStyle(theme)}
            >
              {FONT_WEIGHT_PRESETS.map((preset) => (
                <option key={preset.id} value={preset.value}>
                  {preset.name}
                </option>
              ))}
            </select>
          </div>

          {/* Shadow toggle */}
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <input
              type="checkbox"
              checked={overlay.style?.shadow ?? true}
              onChange={(e) => handleChange('style.shadow', e.target.checked)}
              onClick={(e) => e.stopPropagation()}
              style={{ cursor: 'pointer' }}
            />
