        {/* Cancel Button */}
        <button
          onClick={onClose}
          style={{
            width: '100%',
            padding: '14px',
            borderRadius: '12px',
            border: 'none',
            background: theme.cardBg,
            color: theme.textMuted,
            fontSize: '14px',
            cursor: 'pointer',
            marginTop: '8px',
          }}
        >
