// Tier configuration with limits and pricing
const TIER_CONFIG = {
  free: {
    name: 'Free',
    price: 0,
    videoLimit: 10,
    imageLimit: 100,
    messageLimit: 500,
    features: [
      'Basic AI backgrounds',
